function rankAdd(d, html, obj) {
    var name = obj.name
    var title = obj.title
    var img = obj.img
    var url = obj.url
    var seles = obj.seles
    seles.list = obj.nlist
    seles.title = obj.ntitle
    seles.desc = obj.ndesc


    var list = eval(seles.list)

    list.map((item, i) => {
        i = i + 1
        var stitle = eval(seles.title)
        var de= ''
        if (seles.desc != undefined) {
            de = eval(seles.desc)
            desc = de != '' ? `<font color="#808080">[${de}]</font>` : ''
        }


        var title = i + '.' + eval(seles.title)


        switch (i) {
            case 1:
                title = `““””<small><font color="#ff3d5e">${title}</font> ${desc}</small>`
                break;
            case 2:
                title = `““””<small><font color="#f37000">${title}</font> ${desc}</small>`
                break;
            case 3:
                title = `““””<small><font color="#ffa82e">${title}</font> ${desc}</small>`
                break;
            default:
                title = `““””<small><font color="">${title}</font> ${desc}</small>`
                break;
        }

        d.push({
            title: `${title}`,
            col_type: seles.col_type,
            img: eval(seles.img), 
            desc: eval(seles.d2),
            url: eval(seles.url) + '#immersiveTheme#'+'@rule=js:$.require("hiker://page/ej")',
            extra: {
                pageTitle: stitle,
                state:de
            }
        })
    })
}

