function Decrypt(word) {
    const CryptoUtil = $.require("hiker://assets/crypto-java.js");
    let key = CryptoUtil.Data.parseUTF8("sa5d4s5a64d5qw6e");
    let iv = CryptoUtil.Data.parseUTF8("sa5d4s5a64d5qw6e");
    let decrypted = CryptoUtil.AES.decrypt(word, key, {
        mode: "AES/CBC/PKCS7Padding",
        iv: iv
    });
    return decrypted.toString();
}

eval(getCryptoJS())
const key = CryptoJS.enc.Utf8.parse("sa5d4s5a64d5qw6e");
const iv = CryptoJS.enc.Utf8.parse("sa5d4s5a64d5qw6e");
function Encrypt(word) {
    let encrypt = CryptoJS.AES.encrypt(word, key, {
        iv: iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7,
    });
    let encryptedStr = encrypt.toString(CryptoJS.enc.base64);
    return encryptedStr.toString();
}

//弹幕函数 by cy
function fetchDanmuData(url) {
    var html = request(url); //log(html) 
    var html2 = JSON.parse(html).danmuku; //log(html2)

    var lists = [];
    html2.forEach(item => {
        lists.push(item)
    });
    //log(tabs)
    let danmustr = `<?xml version="1.0" encoding="UTF-8"?>\n<i>\n`;
    for (let i = 0; i < lists.length; i++) {
        var dm0 = lists[i][0]; //log(dm0)
        var dm2 = lists[i][2]; //log(dm2)
        // 颜色设置
            if (lists[i][2] == '#fff') {
                dm2 = 16777215;
            } else {
                dm2 = parseInt(lists[i][2].slice(1), 16);
            }
        var dm4 = lists[i][4]; //log(dm4)
        if (dm4.indexOf('<') != -1 || dm4.indexOf('>') != -1 || dm4.indexOf('&') != -1 || dm4.indexOf('\u0000') != -1 || dm4.indexOf('\b') != -1) {
                continue;
            }
        // 只将实际弹幕数据追加到 `danmustr`
        //时间，顶部弹幕，字体大小，颜色，0，弹幕内容
        let tempdata = `<d p="${dm0},1,25,${dm2},0">${dm4}</d>\n`; //log(tempdata)
        danmustr += tempdata; 
    }
    danmustr += `</i>`;
    writeFile('hiker://files/cache/danmu.xml', danmustr);
}

