    var html = JSON.parse(request(input).match(/r player_.*?=(.*?)</)[1])
    var url = html.url
    if (html.encrypt == '1') {
        url = unescape(url);
    } else if (html.encrypt == '2') {
        url = unescape(base64Decode(url));
    }
    if (/m3u8|mp4/.test(url)) {
        return url
    } else {
        eval(request(MY_HOME + '/static/js/playerconfig.js'));
            var jx = MacPlayerConfig.player_list[html.from].parse;
            if (jx == '') {
                jx = MacPlayerConfig.parse
            }


                eval(request(jx.replace('v.php', 'player/ec.php') + url, {
                    headers: {
                        'Referer': MY_URL
                    }
                }).match(/let ConFig.*}/)[0])
                eval(getCryptoJS())
                return CryptoJS.enc.Utf8.stringify(CryptoJS.AES.decrypt(ConFig.url, CryptoJS.enc.Utf8.parse('2890' + ConFig.config.uid + 'tB959C'), {
                    iv: CryptoJS.enc.Utf8.parse('2F131BE91247866E'),
                    mode: CryptoJS.mode.CBC,
                    padding: CryptoJS.pad.Pkcs7
                })) + '#isVideo=true#'
}
},MY_HOME)

