/*
Ver 2023-10-28 1.0
备注：不要用海阔代码格式化 内容会出错
必须参数：
分类颜色 大类定位 
小类定位 分类标题 分类链接
html =>源码 page =>页码
可选参数：
拼接分类 折叠标记 展开标记 排除
大类链接 大类标题 清除键 开启折叠
附加定位
说明：
外面用getMyVar(MY_RULE.url) 或者 状态 取链接
魔改:α
*/

let empty = "hiker://empty";
let categories = [];
if (typeof (拼接分类) != 'undefined' && 拼接分类 != '') {
    categories = [pdfa(html, 大类定位).join('\n')];
    if (拼接分类.includes(';')) {
        拼接分类 = 拼接分类.split(';');
    }
    if (拼接分类.constructor == Array) {
        拼接分类.forEach(item => {
            categories = categories.concat(pdfa(html, item));
        })
    } else {
        categories = [pdfa(html, 大类定位).join('\n')].concat(pdfa(html, 拼接分类));
    }
    if (typeof (附加定位) != 'undefined' && 附加定位.length > 0) {
        附加定位.forEach(x => {
            var 一级 = pdfa(html, x.一级定位).map(s => {
                var 定位 = pdfa(s, x.小类).map(q => {
                    var title = pdfh(q, x.标题);
                    var url = 'a'
                    if (x.链接.includes('@@')) {
                        state = x.链接.split('@@')[0]
                        value = pdfh(q, x.链接.split('@@')[1]);
                        if (x.转换 == true) {
                            return `<li data-type="${state}" data-val="${value}"><a href="">${title}</a></li>`
                        } else {
                            return `<a type="@extra" state="${state}" value="${value}">${title}</a>`;
                        }
                    } else {
                        href = pd(q, x.链接);
                        return `<a href="${href}">${title}</a>`;
                    }
                });
                return 定位;
            });
            categories.push(一级.join('\n'));
        });
    }
} else {
    categories = [pdfa(html, 大类定位).join('\n')];
}

let items = [];
