                        storage0.putMyVar('cate_button', cx);
                        deleteItemByCls(title + 'cate_button');
                    } else {
                        var cx = storage0.getMyVar('cate_button', []).map(x => {
                            x['col_type'] = x['col_type'] ? x['col_type'] : x['type'];
                            return x;
                        });
                        addItemAfter(title + 'cate_line', cx)
                    }
                    /*all.forEach((item) => {
                      clearMyVar(item);
                    });*/
                    updateItem(title + 'cate_exp', {
                        title: fold == 1 ? 展开 : 折叠,
                    })
                    putMyVar(MY_RULE.group, fold === "1" ? "0" : "1");
                    return "hiker://empty";
                }, MY_RULE.title, fold, 状态, 全清, [展开, 折叠]),
                col_type: 'scroll_button',
