let history;
try {
    history = JSON.parse(readFile("WordHistory.json", 0) || "[]");
} catch (e) {
    history = [];
}

function setHistory(title) {
    if (history.length >= 20) {
        history.splice(0, 1);
    }
    let index = history.indexOf(title);

    if (index > -1) {
        history.splice(index, 1);
    }
    history.push(title);
    saveFile("WordHistory.json", JSON.stringify(history), 0);
}

function deleteHistory(title) {
    let index = history.indexOf(title);
    if (index > -1) {
        history.splice(index, 1);
        saveFile("WordHistory.json", JSON.stringify(history), 0);
    }
}

function getHistory() {
    return history;
}
function clearHistory() {
    saveFile("WordHistory.json", "[]", 0);
}
$.exports = {
    setHistory: setHistory,
    getHistory: getHistory,
    clearHistory: clearHistory,
    deleteHistory: deleteHistory
}"},{"col_type":"movie_3","name":"搜索结果","path":"搜索结果","rule":"js:
