let basicUrl = "https://api.dandanplay.net/";
let sevePath = "hiker://files/cache/danmu/";

function getRefreshRate() {
    const Context = android.content.Context;
    const Display = android.view.Display;
    const WindowManager = android.view.WindowManager;
    const context = com.example.hikerview.ui.Application().getContext();

    let windowManager = context.getSystemService(Context.WINDOW_SERVICE);

    let display = windowManager.getDefaultDisplay();

    let refreshRate = display.getRefreshRate();
    return refreshRate;
}
$.exports.getDanMu = function(key) {
    return JSON.parse(request(basicUrl + "api/v2/search/episodes?anime=" + encodeURIComponent(decodeURIComponent(key))));
}

function encodeHtml(s) {
    let REGX_HTML_ENCODE = /“|&|’|\u003C|>|[\x00-\x20]|[\x7F-\xFF]|[\u0100-\u2700]/g;
   return s.replace(REGX_HTML_ENCODE, function($0) {
        var c = $0.charCodeAt(0),
            r = ["&#"];
        c = (c == 0x20) ? 0xA0 : c;
        r.push(c);
        r.push(";");
        return r.join("");
    });
}



//0默认 1简体 2繁体
$.exports.downloadDanMu = function(episodeId, name) {
    let danMuData = JSON.parse(request(buildUrl(basicUrl + "api/v2/comment/" + episodeId, {
        withRelated: true,
        chConvert: 1
    })));
    let dMcontext = "";
    //let fpsm = getRefreshRate()/60*0.95;
    //danMuData.comments.sort((v,x)=>v.p.split(",")[0]-x.p.split(",")[0]);
    for (let comment of danMuData.comments) {
        //dMcontext += `<d p="${Math.round(timepoint)},${ct},${size},${color},0">${content}</d>\n`;
        let paramText = comment.p;
        let commentText = comment.m;
        if (paramText == null || commentText == null) continue;
        let params = paramText.split(",");
        let paramsT = [];
        for (let i in params) {
            /*if (i == 0) {
                paramsT.push(parseInt(params[i]));
                continue;
            }*/
            if (i == 2) {
                paramsT.push("25");
            }
            if (i == 3) {
                //颜色值为0时，默认设置为白色
                if (params[i] == "0" || params[i] == "-1") {
                    paramsT.push("16777215");
                    continue
                }
                break;
            }
            paramsT.push(params[i]);
        }
        paramsT.push("0");
        let times=parseInt(params[0]/60)+":"+parseInt(params[0]%60);
        dMcontext += `<d p="${paramsT.join(',')}">${encodeHtml(commentText)+"#"+times}</d>\n`;
    }
    if (!dMcontext) return;
    dMcontext = `<?xml version="1.0" encoding="UTF-8"?><i>\n${dMcontext}</i>`;
    let path = sevePath + name.replace(/\//g, "_") + "_" + episodeId + ".xml";
    writeFile(path, dMcontext);
    return path;
}
$.exports.getLocalDanMu = function(episodeId, name) {
    let path = sevePath + name.replace(/\//g, "_") + "_" + episodeId + ".xml";
    if (fileExist(path)) {
        return path;
    }
}"}]

