function getNextHeaderFromJSONArray(key, array) {
    if (array == undefined || array.length == 0) {
        return;
    }
    let nextHeader = {};
    let defaultValue = "";
    defaultValue = array[0];
    if (Object.prototype.toString.call(array[0]) === '[object Object]') {
        defaultValue = Object.keys(array[0])[0];
    }
    putKeyIntoMap("cryptor@@" + key, getVar("cryptor@@" + key, defaultValue))
    putVar("cryptor_cur_index", key + "@@" + getVar("cryptor@@" + key, defaultValue));
    for (let value of array) {
        if (Object.prototype.toString.call(value) === '[object Object]') {
            // log("valueKeys: "+JSON.stringify(Object.keys(value)[0]))
            let valueKey = Object.keys(value)[0];
            d.push({
                title: valueKey == getVar("cryptor@@" + key, defaultValue) ? "â€œâ€œ" + valueKey + "â€â€" : valueKey,
                url: $("#noLoading#").lazyRule((valueKey, key) => {
                    function putKeyIntoMap(key, value) {
                        const keyMap = JSON.parse(getVar("cryptor_all_key_Map", "{}"));
                        keyMap[key] = value;
                        putVar("cryptor_all_key_Map", JSON.stringify(keyMap));
                    }
                    putVar("cryptor@@" + key, valueKey);
                    putVar("cryptor_cur_index", key + "@@" + valueKey);
                    putKeyIntoMap("cryptor@@" + key, getVar("cryptor@@" + key, "defaultValue"))
                    refreshPage(false);
                    return "hiker://empty"
                }, valueKey, key),
                col_type: "scroll_button"
            });
            nextHeader[valueKey] = value[valueKey];
        } else {
            d.push({
                title: value == getVar("cryptor@@" + key, defaultValue) ? "â€œâ€œ" + value + "â€â€" : value,
                url: $("#noLoading#").lazyRule((value, key) => {
                    function putKeyIntoMap(key, value) {
                        const keyMap = JSON.parse(getVar("cryptor_all_key_Map", "{}"));
                        keyMap[key] = value;
                        putVar("cryptor_all_key_Map", JSON.stringify(keyMap));
                    }
                    putVar("cryptor@@" + key, value);
