    /**
     * 格式化时间戳为标准时间
     * @param {number|string} timestamp - 秒级或毫秒级时间戳（支持科学计数法）
     * @returns {string} - 返回格式化后的时间 "YYYY-MM-DD HH:MM:SS"
     */
    formatTimestamp: (timestamp) => {
        // 统一将时间戳解析为数字
        const ts = Number(timestamp);
        if (isNaN(ts)) {
            throw new Error("Invalid timestamp format");
        }

        // 判断时间戳是秒级（10位）还是毫秒级（13位）
        const millis = ts < 1e12 ? ts * 1000 : ts;

        // 创建日期对象
        const date = new Date(millis);

        // 格式化日期和时间，确保补零
        const pad = (num) => String(num).padStart(2, '0');
        return `${date.getFullYear()}-${pad(date.getMonth() + 1)}-${pad(date.getDate())} ` +
            `${pad(date.getHours())}:${pad(date.getMinutes())}:${pad(date.getSeconds())}`;
    },
    Decrypt: (word,keyy) => {
        let CryptoUtil = $.require("hiker://assets/crypto-java.js");
        let key = CryptoUtil.Data.parseUTF8(keyy);
