    lazy: (input) => {
        var lazy = $(input).lazyRule(() => {
            function getUnixTimestamp() {
                const now = new Date();
                // 获取年月日时分，确保秒和毫秒都为 0
                now.setSeconds(0, 0);
                // 获取 10 位 Unix 时间戳
                const timestamp = Math.floor(now.getTime() / 1000);
                // 强制转换为字符串后返回
                return timestamp.toString();
            }
            var params1 = JSON.stringify({"m":"play","aid":base64Decode(input).split('_')[2],"pid":base64Decode(input).split('_')[3],"t":md5(md5('play'+base64Decode(input).split('_')[2]+base64Decode(input).split('_')[3])+'1449682949'+getUnixTimestamp())}); //log(params1) 
