function getHeaderFromJSONObject(obj) {

    var getRangeColors = function() {
        return ('00000' + (Math.random() * 0x1000000 << 0).toString(16)).substr(-6);
    }
    var tindex = storage0.getMyVar('tindex', 0)
    var titles = ["首页", "分类", "书架"]
    var imgs = ["https://img.vinua.cn/images/IdlUv.th.png", "https://img.vinua.cn/images/Idqlk.th.png", "https://img.vinua.cn/images/Id29J.th.png"]
    titles = titles.map((x, i) => {
        var title = i == tindex ? `<b><font color="#9370DB">${x}</font></b>` : x
        return title
    })
    if (MY_PAGE == 1) {
        for (var i = 0; i < titles.length; i++) {
            var mytitle = titles[i];
            d.push({
                title: mytitle,
                url: $('#noLoading#').lazyRule((i) => {
                    storage0.putMyVar('tindex', i)
                    refreshPage()
                    return 'hiker://empty'
                }, i),
                img: imgs[i],
                col_type: "icon_small_3"
            });
        }
    }

    for (let key in obj) {
        d.push({
            title: key == getVar("cryptor", Object.keys(obj)[0]) ? "““" + key + "””" : key,
            url: $("#noLoading#").lazyRule((key) => {
                putVar("cryptor", key);
                putVar("cryptor_cur_index", key);
                refreshPage(false);
