    var cookie = getItem(MY_HOME, "")
    let muid = input.match(/sound\/(\d+)/)[1];
    let timestamp = new Date().getTime();
    let furl = "https://mobile.ximalaya.com/mobile-playpage/track/v3/baseInfo/" + timestamp + "?device=web&trackId=" + muid;
    let headers = {
        "User-Agent": "Mozilla/5.0 (Linux; U; Android 9; zh-cn; MIX 2S Build/PKQ1.180729.001) AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/100.0.4896.127 Mobile Safari/537.36 XiaoMi/MiuiBrowser/16.7.35 swan-mibrowser",
        "Cookie": cookie,
        "Content-Type": "application/x-www-form-urlencoded"
    };
    if (tag == 0 || tag == 1) {
        try {
            var jhtml = fetch(furl, {
                headers: headers,
                method: "get"
            });
            var playlist = JSON.parse(jhtml);
        } catch (e) {
            var playlist = JSON.parse(fetch("http://jx.nokia.press/api6/xmly?" + "ts=" + timestamp + "&muid=" + muid));
        }
    } else {
        var playlist = JSON.parse(fetch("http://jx.nokia.press/api6/xmly?" + "ts=" + timestamp + "&muid=" + muid));
    }
    try {
        function base64UrlDecode(s) {
            s = s.replace(/-/g, "+").replace(/_/g, "/");
            switch (s.length % 4) {
                case 0:
                    break;
                case 2:
                    s += "==";
                    break;
                case 3:
                    s += "=";
                    break;
                default:
                    throw new Error("Illegal base64url String!");
            }
            const javaString = java.lang.String;
            let bytes = _base64.decode(s, _base64.NO_WRAP);
            let javaImport = new JavaImporter();
            javaImport.importPackage(Packages.com.alibaba.fastjson);
            return javaImport.JSON.toJSONString(bytes).replace("\"", "") + "";
        }

        function De(url) {
            eval(getCryptoJS());
            var cipherText = base64UrlDecode(url);
            log(url)
            log(cipherText)
            let res = CryptoJS.AES.decrypt(cipherText, CryptoJS.enc.Hex.parse("aaad3e4fd540b0f79dca95606e72bf93"), {
                mode: CryptoJS.mode.ECB,
                padding: CryptoJS.pad.Pkcs7
            });
            return res.toString(CryptoJS.enc.Utf8);
        }
        let playdata = playlist.trackInfo.playUrlList;
        var types = [];
        var urls = [];
        for (let i = 0; i < playdata.length; i++) {
            types.push(playdata[i].type);
            urls.push(De(playdata[i].url));
        }
        return JSON.stringify({
            urls: urls,
            names: types
        });
        //return De(playdata[0].url)
    } catch (e) {
        return "toast://\u4ed8\u8d39\u5185\u5bb9\uff0c\u65e0\u6cd5\u64ad\u653e\uff01";
    }

}, MY_HOME)

function setDesc(d, desc, num,detail) {
    //log(desc)
    if (desc == undefined) {
        return;
    }
    desc = desc.constructor == Array ? desc.join('<br>') : desc;
    if (desc.replace(/(<br>|\s+|<\/?p>|&nbsp;)/g, '').length == 0) {
        return;
    }
    const mark = 'desc';
    num = typeof(num) == 'undefined' ? 100 : num


    function substr(str, maxLength) {
        let len = 0;
        for (let i = 0; i < str.length; i++) {
            if (str.charCodeAt(i) > 225) {
                len += 2;
            } else {
                len++;
            }
            if (len > maxLength) {
                return str.slice(0, i) + '...';
            }
        }
        return str;
    }


    var colors = {
        show: "black",
        hide: "grey"
    }

     sdesc= detail


    var lazy = $(`#noLoading#`).lazyRule((dc, sdc, m, cs) => {
        var show = storage0.getItem(m, '0');
        var title = findItem('desc').title;
        var re = /(<\/small><br>.*?>).+/g;
        var exp = '收起:';
        var ret = '展开:';
        if (show == '1') {
            updateItem('desc', {
                title: title
                    .replace(ret, exp)
                    .replace(re, '$1' + sdc + '</small>')
                    .replace(/(<\/small><br>\<font color=").*?(">)/, '$1' + cs.hide + '$2')
            })
            storage0.setItem(m, '0');
        } else {
            updateItem('desc', {
                title: title
                    .replace(exp, ret)
                    .replace(re, '$1' + dc + '</small>')
                    .replace(/(<\/small><br>\<font color=").*?(">)/, '$1' + cs.show + '$2')
            })
            storage0.setItem(m, '1');
        }
        return `hiker://empty`
    }, desc, sdesc, mark, colors)
    var sc = storage0.getItem(mark, '0') == '0' ? '收起:' : '展开:';
    var dc = storage0.getItem(mark, '0') == '0' ? sdesc : desc;
