        let increasingCount = 0;
        let decreasingCount = 0;
        for (let i = 1; i < numbers.length; i++) {
            if (numbers[i] > numbers[i - 1]) {
                increasingCount++;
            } else if (numbers[i] < numbers[i - 1]) {
