var {
    QING_TITLE, title, url,
    class_name, class_url,
    area_name, area_url,
    year_name, year_url,
    sort_name, sort_url
} = $.importParam
const ClassTab = function(classArray, params) {
    Object.assign(this, params)
    this.arr = classArray.map(v => this.init(v))
    this.sign = '$' + QING_TITLE + '_' + (this.name || '') + '_'
    this.color = this.color || '#12b668'
    this.boundary = this.boundary || 'blank_block'
}
ClassTab.prototype = {
    constructor: ClassTab,
    load(el) {
        let folded = this.fold && getVar('$fold_' + this.sign, '')
        if (this.arr.length > 1 && this.fold) el.push({
            title: '““””' + (folded ? '▶' : '▼').fontcolor('#049eff'),
            url: $('#noLoading#').lazyRule((sign, folded) => {
                putVar('$fold_' + sign, folded ? '' : 'T')
                refreshPage(false)
                return 'hiker://empty'
            }, this.sign, folded),
            col_type: 'scroll_button'
        })
        let arr = folded ? [this.arr[0]] : this.arr
        arr.forEach((v) => {
            let { id, class_name, class_url } = v,
            selected = JSON.stringify(this.getClass(id))
            class_name.forEach((name, i) => {
                let url = class_url[i],
                    now = JSON.stringify({ name: name, url: url })
                el.push({
                    title: selected == now ? '““””' + name.fontcolor(this.color).bold() : name,
                    url: selected == now ? 'hiker://empty' : $('#noLoading#').lazyRule((sign, id, now) => {
                        putVar(sign + id, now)
                        refreshPage(false)
                        return 'hiker://empty'
                    }, this.sign, id, now),
                    col_type: 'scroll_button'
                })
            })
            el.push({ col_type: this.boundary })
        })
    },
    init(classObject) {
        if (typeof classObject.class_name == 'string')
            classObject.class_name = classObject.class_name.split('&')
        if (typeof classObject.class_url == 'string')
            classObject.class_url = classObject.class_url.split('&').map((v) => v.trim())
        return classObject
    },
    push(classObject) {
        this.arr.push(this.init(classObject))
    },
    getClass(id) {
        let defaultClass = this.arr.find(item => item.id == id)
        defaultClass = JSON.stringify(defaultClass ? {
            name: defaultClass.class_name[0],
            url: defaultClass.class_url[0]
        } : { name:  'cannot find id: ' + id + ' in classTab: ' + this.sign, url: ''})
        return JSON.parse(getVar(this.sign + id, defaultClass))
    },
    setUrl(url) {
        return url.replace(/\$\{([^}]*)\}/g, (_, id) => this.getClass(id).url)
    }
}
// 生成头部分类
let fyAll = url.includes('fyAll'),
    tabHeader = []
if (class_name) tabHeader.push({
    id: fyAll ? 'fyAll' : 'fyclass',
    class_name: class_name,
    class_url: class_url
})
if (area_name) tabHeader.push({
    id: fyAll ? 'fyAll' : 'fyarea',
    class_name: area_name,
    class_url: area_url
})
if (year_name) tabHeader.push({
    id: fyAll ? 'fyAll' : 'fyyear',
    class_name: year_name,
    class_url: year_url
})
if (sort_name) tabHeader.push({
    id: fyAll ? 'fyAll' : 'fysort',
    class_name: sort_name,
    class_url: sort_url
})
$.exports = {
    fyAll: fyAll,
    tabHeader: new ClassTab(tabHeader, { name: title, fold: readFile('tabFold'), })
}

js:
    addListener('onClose', 'clearMyVar("sortFlag");refreshPage()')
setPageTitle('⚙ 道德经设置 ⚙')
const QING_TITLE = getMyVar('myCollection'),
    url = getMyVar('remoteUrl')
let data = $.require('hiker://page/dataLoad?rule=' + QING_TITLE, true),
    el = [{
        title: '<b> 清除缓存，立即更新 <small> （基于远程仓库，不要频繁点击）',
        url: $('#noLoading#').lazyRule((url) => {
            if (url.startsWith('hiker://') || url.startsWith('file://'))
                return 'toast://仓库为本地文件，不会进行缓存，本功能也无效'
            deleteCache(url)
            back(false)
            return 'toast://已更新'
        }, url),
        img: 'hiker://images/icon4',
        col_type: 'avatar'
    }],
    updateInterval = readFile('updateInterval') || '1',
    newWindow = readFile('newWindow'),
    tabFold = readFile('tabFold'),
    disableCustom = readFile('disableCustom'),
    editMode = disableCustom ? '导入海阔' : getMyVar('editMode', '启用/禁用')

el.push({
    title: '  💡 更新频率: ' + (updateInterval == 0 ? '手动' : updateInterval + '天') + '  ',
    url: $(updateInterval, '输入更新间隔（天），为0则手动更新\n本地文件链接无效').input(() => {
        input = parseInt(input)
        if (Number.isNaN(input)) input = 1
        else if (input < 1) input = 0
        saveFile('updateInterval', input.toString())
        refreshPage()
        return 'hiker://empty'
    }),
    col_type: 'scroll_button'
}, {
    title: '  🖥 独立首页: ' + (newWindow ? '是' : '否') + '  ',
    url: $('#noLoading#').lazyRule((newWindow) => {
        saveFile('newWindow', newWindow ? '' : 'T')
        refreshPage()
        return 'hiker://empty'
    }, newWindow),
    col_type: 'scroll_button'
}, {
    title: '  💠 分类折叠: ' + (tabFold ? '是' : '否') + '  ',
    url: $('#noLoading#').lazyRule((tabFold) => {
        saveFile('tabFold', tabFold ? '' : 'T')
        refreshPage()
        return 'hiker://empty'
    }, tabFold),
    col_type: 'scroll_button'
}, {
    title: '  💼 备份恢复  ',
    url: $(['备份', '恢复'], 1).select((QING_TITLE, updateInterval, newWindow, tabFold, disableCustom) => {
        let url = getMyVar('remoteUrl')
        if (!url.startsWith('hiker://') && !url.startsWith('file://'))
            url = 'hiker://files/libs/' + md5(url) + '.js'
        else if (url.startsWith('hiker://page/')) url = 'hiker://empty'
        if (input == '备份') return $('hiker://files/tmp/' + QING_TITLE + '_backup.json', '输入备份路径')
            .input((url, updateInterval, newWindow, tabFold, disableCustom) => {
                let rules = request(url),
                    customData = readFile('customData')
                writeFile(input, JSON.stringify({
                    updateInterval: updateInterval,
                    newWindow: newWindow,
                    tabFold: tabFold,
                    disableCustom: disableCustom,
                    customData: customData,
                    rules: rules
                }))
                return 'toast://已备份，注意若仓库为子页面则不备份'
            }, url, updateInterval, newWindow, tabFold, disableCustom)
        else return $('hiker://files/tmp/' + QING_TITLE + '_backup.json', '输入备份路径').input((url) => {
            let { updateInterval, newWindow, tabFold, disableCustom, customData, rules } = JSON.parse(request(input))
            saveFile('updateInterval', updateInterval)
            saveFile('newWindow', newWindow)
            saveFile('tabFold', tabFold)
            saveFile('disableCustom', disableCustom)
            saveFile('customData', customData)
            if (rules) writeFile(url, rules)
            refreshPage()
            return 'toast://已恢复，如果不想同步远程仓库，可以设置更新频率为手动'
        }, url)
    }, QING_TITLE, updateInterval, newWindow, tabFold, disableCustom),
    col_type: 'scroll_button'
}, {
    title: '““””<font color="#666666"><small>特别鸣谢，小程序提供者：\n' +
        data.reduce((self, v) => v.author ? self.concat(v.author.split('&')) : self, [])
            .filter((v, i, arr) => v && arr.indexOf(v) == i).join('、'),
    url: 'hiker://empty',
    col_type: 'text_center_1'
})

el.push({ col_type: 'line' }, {
    title: '““””' + '📝'.small() + '&nbsp 轻合集自定义 &nbsp'.bold().fontcolor('#757575') + (disableCustom ? '❌' : '⭕').small(),
    url: $([(disableCustom ? '启用' : '禁用') + '合集自定义', '清除自定义数据', '编辑JSON'], 1).select(() => {
        switch (input.slice(0, 2)) {
            case '清除':
                deleteFile('customData')
                break
            case '启用':
                saveFile('disableCustom', '')
                break
            case '禁用':
                saveFile('disableCustom', 'T')
                input += '\n小程序启用/禁用、重新排序等操作均不会体现在首页小程序列表中'
                break
            case '编辑':
                let url = getMyVar('remoteUrl')
                if (!url.startsWith('hiker://files/') && !url.startsWith('file://'))
                    url = 'hiker://files/libs/' + md5(url) + '.js'
                return  'hiker://page/interface#noRefresh##noHistory##noRecordHistory#?rule=JSON编辑器&Json='+base64Encode(url)
        }
        refreshPage(false)
        return 'toast://已' + input
    }),
    col_type: 'text_center_1',
    extra: { lineVisible: false }
});
(disableCustom ? ['导入海阔'] : ['启用/禁用', '重新排序', '更改图标', '导入海阔']).forEach((v) => {
    el.push({
        title: v == editMode ? '““””' + v.bold().fontcolor('#12b668') : v,
        url: v == editMode ? 'hiker://empty' : $('#noLoading#').lazyRule((v) => {
            putMyVar('editMode', v)
            refreshPage(false)
            return 'hiker://empty'
        }, v),
        col_type: 'scroll_button'
    })
});
var datas = (disableCustom ? data : JSON.parse(readFile('customData') || '[]'));
let sk = getMyVar('importSearchKey','');
if(editMode === '导入海阔'&&sk) {
    datas = datas.filter(it=>it.title.includes(sk)||(it.url&&it.url.includes(sk)));
    data = data.filter(it=>it.title.includes(sk)||(it.url&&it.url.includes(sk)));
}
if(editMode === '导入海阔') {
    el.push({
        title:'搜索',
        desc:'输入小程序标题|地址进行搜索...',
        url:$.toString(()=>{
            refreshPage(false);
        }),
        col_type:'input',
        extra: {
            onChange: "putMyVar('importSearchKey',input)",
            titleVisible: true,
            defaultValue:getMyVar('importSearchKey','')
        },
    });
    el.push({
        title: '全部导入',
        url:$('#noLoading#').lazyRule((sk,url) => {
            let prex_code = '海阔视界￥home_rule_url￥';
            if(sk){
                let file = 'hiker://files/cache/道德经过滤导入.json';
                let data = $.require('hiker://page/dataLoad?rule=' + getMyVar('myCollection'), true);
                data = data.filter(it=>it.title.includes(sk)||(it.url&&it.url.includes(sk)));
                writeFile(file, JSON.stringify(data));
                return prex_code + file
            }else{
                return prex_code + (url.startsWith('hiker://') || url.startsWith('file://') ? url : 'hiker://files/libs/' + md5(url) + '.js')
            }

        }, sk,url),
        col_type: 'text_center_1',
        extra: {lineVisible: false}
    });
}

datas.forEach((v, i) => {
    let d = { title: v.title, img: data[i].icon }
    switch (editMode) {
        case '启用/禁用':
            d.title = (v.visible ? '🟢  ' : '🔴  ') + d.title
            d.url = $('#noLoading#').lazyRule((i) => {
                let rules = JSON.parse(readFile('customData') || '[]')
                rules[i].visible = !rules[i].visible
                saveFile('customData', JSON.stringify(rules))
                refreshPage(false)
                return 'hiker://empty'
            }, i)
            break
        case '重新排序':
            let sortFlag = parseInt(getMyVar('sortFlag', '-1'))
            d.title = (sortFlag == i ? '🔃  ' : '') + d.title
            if (sortFlag == -1)
                d.url = $('#noLoading#').lazyRule((i) => {
                    putMyVar('sortFlag', i.toString())
                    refreshPage(false)
                    return 'toast://选择要移动到的位置'
                }, i)
            else
                d.url = $('#noLoading#').lazyRule((oldIndex, newIndex) => {
                    let rules = JSON.parse(readFile('customData') || '[]')
                    rules.splice(newIndex, 0, rules.splice(oldIndex, 1)[0])
                    saveFile('customData', JSON.stringify(rules))
                    putMyVar('sortFlag', '-1')
                    refreshPage(false)
                    return 'hiker://empty'
                }, sortFlag, i)
            break
        case '更改图标':
            d.url = $(v.icon || '', '输入新图标地址或颜色代码：').input((i) => {
                let rules = JSON.parse(readFile('customData') || '[]')
                if (input)
                    rules[i].icon = input
                else
                    delete rules[i].icon
                saveFile('customData', JSON.stringify(rules))
                refreshPage(false)
                return 'hiker://empty'
            }, i)
            break
        case '导入海阔':
            d.url = $('#noLoading#').lazyRule((i,sk) => {
                let data = $.require('hiker://page/dataLoad?rule=' + getMyVar('myCollection'), true);
                data = data.filter(it=>it.title.includes(sk)||(it.url&&it.url.includes(sk)));
                return 'rule://' + base64Encode(JSON.stringify(data[i]))
            }, i,sk)
            break
    }
    el.push(d)
})
setResult(el)

js:
    const QING_TITLE = getMyVar('myCollection')
var myColl_el = []
eval(JSON.parse(fetch('hiker://page/hijackEnv?rule=' + QING_TITLE)).rule)

if (!isIndex) {
    if (pageTitle) setPageTitle(pageTitle)
    setLastChapterRule(oriRULE.last_chapter_rule)
    MY_PARAMS = MY_RULE.params
} else if (MY_PAGE == 1) {
    tabHeader.load(myColl_el)
    if (RULE.search_url) myColl_el.push({
        title: "搜索",
        desc: "搜你想要的...",
        url: '"hiker://search?s="+input',
        extra: {
            rules: $.toString((QING_TITLE, title,nowData) => {
                let rules = []
                let r = {}
                if (nowData) {
                    let pt = 'hiker://files/rules/dzHouse/ruleCache/' + nowData + '.json';
                    r = JSON.parse(fetch(pt)).find((v) => v.title == title)
                } else {
                    r = $.require('hiker://page/dataLoad?rule=' + QING_TITLE).find((v) => v.title == title)
                }
                rules = [r]
                return JSON.stringify(rules)
            }, QING_TITLE, RULE.title,nowData),
            defaultValue: getMyVar('searchKey', ''),
            onChange: "putMyVar('searchKey',input)"
        },
        col_type: "input",
    })
}
// 正文解析
var {
    paramHandle,
    runPreRule,
    genExUrl,
    hijackLazyRule
} = $.require('hiker://page/funcTools?rule=' + QING_TITLE)
runPreRule(oriRULE.preRule)
if (MY_RULE.find_rule.startsWith('js:')) {
    let { hijackEl } = $.require('hiker://page/funcTools?rule=' + QING_TITLE)
    setResult = function(param0, param1, param2, param3) {
        param1 = CALLBACK_KEY
        param2 = MY_RULE
        param3 = MY_TYPE
        if (Array.isArray(param0.data)) param0 = param0.data
        Array.prototype.push.apply(myColl_el, param0.map(hijackEl))
        method_setResult.invoke(javaContext, myColl_el, param1, param2, param3)
    }
    setHomeResult = setResult
    setSearchResult = setResult

    eval(MY_RULE.find_rule.slice(3))
} else {
    let { runCode } = $.require('hiker://page/funcTools?rule=' + QING_TITLE),
        [_, findRule, detailFindRule] = MY_RULE.find_rule.match(/^([\s\S]*?)(?:==>([\s\S]*))?$/)
    findRule = findRule.split(';')
    parseDomForArray(getResCode(), findRule.shift()).forEach((data) => {
        let [title, img, desc, url] = findRule.map((v, i) => {
                try {
                    if (v == '*') return ''
                    else v = (i == 1 || i == 3) ?
                        parseDom(data, v) :
                        parseDomForHtml(data, v)
                    if (i != 3) v = runCode(v)
                    return v
                } catch (e) { return '' }
            }),
            res = {
                title: title, url: url,
                desc: desc, img: img,
                col_type: MY_RULE.col_type
            }
        if (res.url) {
            if (res.url.includes('@lazyRule=')) {
                let reIndex = res.url.indexOf('.js:')
                if (reIndex >= 0) res.url = res.url.slice(0, reIndex) + '.js:' + hijackLazyRule + ';' + res.url.slice(reIndex + 4)
            } else  if (detailFindRule) {
                res.extra = {
                    url: genExUrl(url),
                    RULE: base64Encode(JSON.stringify(Object.assign(genMyRule(), { detail_find_rule: detailFindRule }))),
                    pageTitle: isIndex && title,
                    nowData: nowData
                }
                res.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
            } else if (MY_RULE.detail_find_rule) {
                res.extra = {
                    url: genExUrl(url),
                    RULE: base64Encode(JSON.stringify(genMyRule())),
                    pageTitle: isIndex && title,
                    nowData: nowData
                }
                res.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
            }
        }
        myColl_el.push(res)
    })
    setResult(myColl_el)
}

let showAll = $.importParam,
    url = getMyVar('remoteUrl'),
    data = []
if (url.startsWith('hiker://') || url.startsWith('file://')) {
    // 如果为本地文件则不进行缓存
    try {
        let oriData = fetch(url)
        if (url.startsWith('hiker://page/'))
            oriData = JSON.parse(oriData).rule
        data = JSON.parse(oriData)
    } catch (e) {}
} else {
    // 获取远程数据，检查间隔默认一天
    let interval = parseInt(readFile('updateInterval') || '1'),
        oldData = fetch('hiker://files/libs/' + md5(url) + '.js'),
        remoteData = []
    if (interval == 0) interval = -1
    try { remoteData = JSON.parse(fetchCache(url, 24 * interval, { timeout: 50000 })) } catch (e) {}
    if (!Array.isArray(remoteData)) remoteData = []
    if (remoteData.length < 3) {
        log('未获取到数据')
        if (oldData) writeFile('hiker://files/libs/' + md5(url) + '.js', oldData)
        data = JSON.parse(oldData || '[]')
    } else {
        log('已获取到数据')
        data = remoteData
    }
}
data = data.filter((v) => v.title != getMyVar('myCollection') && !(v.author || '').includes('轻合集生成器'))
if (readFile('disableCustom')) $.exports = data
else { // 写入自定义数据
    let customData = JSON.parse(readFile('customData') || '[]'),
        rewriteData = []
    customData = customData.reduce((self, v) => {
        let index = data.findIndex((vv) => v.title == vv.title)
        if (index >= 0) {
            self.push(v)
            let rule = data.splice(index, 1)[0]
            if (showAll || v.visible)
                rewriteData.push(Object.assign(rule, v))
        }
        return self
    }, [])
    data.forEach((v) => customData.push({ title: v.title, visible: true }))
    saveFile('customData', JSON.stringify(customData))
    $.exports = rewriteData.concat(data)
}

