    } = $.require('hiker://page/funcTools?rule=' + QING_TITLE)
}, QING_TITLE, base64Encode(JSON.stringify({ url: MY_RULE.url, RULE: base64Encode(JSON.stringify(genMyRule())), isIndex: isIndex,nowData:nowData })))

function hijackEl(el) {
    if(!el || typeof el != 'object') return
    if (!el.extra) el.extra = {}
    el.col_type = el.col_type || MY_RULE.col_type
    let NOW_RULE = Object.assign(genMyRule(), { params: el.extra })

    if (typeof el.url != 'string' ||
        ['x5_webview_single', 'input'].some((v) => el.col_type == v) ||
        ['海阔视界', 'javascript:'].some((v) => el.url.startsWith(v)) ||
        ['rule', 'pics', 'toast', 'copy', 'editFile', 'x5', 'x5WebView', 'x5Play', 'web', 'x5Rule', 'webRule',
            'download', 'share', 'fileSelect', 'video'].some((v) => el.url.startsWith(v + '://'))) {
        return el
    } else if (el.url.startsWith('input://')) {
        let para = JSON.parse(el.url.slice(8))
        if (para.js) para.js = hijackLazyRule + ';' + para.js
        el.url = 'input://' + JSON.stringify(para)
        return el
    } else if (el.url.startsWith('confirm://')) {
        let reIndex = el.url.indexOf('.js:')
        if (reIndex >= 0) el.url = el.url.slice(0, reIndex) + '.js:' + hijackLazyRule + ';' + el.url.slice(reIndex + 4)
        return el
    } else if (el.url.startsWith('select://')) {
        let para = JSON.parse(el.url.slice(9))
        if (para.js) para.js = hijackLazyRule + ';' + para.js
        el.url = 'select://' + JSON.stringify(para)
        return el
    } else if (el.url.includes('@rule=')) {
        let [_, url, rule] = el.url.match(/^([\s\S]*?)@rule=([\s\S]*)$/)
        el.url = url
        NOW_RULE.detail_find_rule = rule
        NOW_RULE.detail_col_type = MY_RULE.col_type
    } else if (el.url.startsWith('hiker://page/')) {
        if (el.url.includes('rule=') || el.extra.rule)
            return el
        let [_, path, flag, params] = el.url.match(/^hiker:\/\/page\/(.+?)(#.*?)?(?:\?(.*))?$/),
            subPage = MY_RULE.pageList.find((v) => v.path == path),
            subUrl = (params || '').split('&').find((v) => v.startsWith('url='))
        el.url = (subUrl ? subUrl.slice(4).replace(/？？/g, '?').replace(/＆＆/g, '&') : (el.extra || {}).url) || 'hiker://empty' + flag + '?' + (params || '')
        NOW_RULE.detail_find_rule = subPage.rule
        NOW_RULE.detail_col_type = subPage.col_type
    } else if (el.url.includes('@lazyRule=')) {
        let reIndex = el.url.indexOf('.js:')
        if (reIndex >= 0) el.url = el.url.slice(0, reIndex) + '.js:' + hijackLazyRule + ';' + el.url.slice(reIndex + 4)
        return el
    } else if (isIndex) {
        if (!MY_RULE.detail_find_rule || /hiker:\/\/(?!empty.+$)/.test(el.url)) return el
    } else {
        return el
    }
    el.extra = Object.assign({}, el.extra, {
        url: genExUrl(el.url),
        RULE: base64Encode(JSON.stringify(NOW_RULE)),
        pageTitle: isIndex && el.title,
        nowData: nowData
    })
    el.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
    return el
}
$.exports = {
    runCode: runCode,
    indexUrl: indexUrl,
    paramHandle: paramHandle,
    runPreRule: runPreRule,
    genMyRule: genMyRule,
    genExUrl: genExUrl,
    hijackLazyRule: hijackLazyRule,
    hijackEl: hijackEl
}
