js:
function doubleTreating(openMode, findAliUrl, title) {
    let aliUrl = input;
    let sharePwd;
    if (findAliUrl) {
        let result = findAliUrl(aliUrl);
        if (typeof result === "string")
            aliUrl = result;
        else
            aliUrl = result.aliUrl, sharePwd = result.sharePwd;
    }
    if (aliUrl.includes("www.aliyundrive.com/s/")) {
        if (openMode === "本地转码") {
            return 'hiker://page/aliyun?page=fypage&rule=' + title + '&realurl=' + encodeURIComponent(aliUrl) + "&sharePwd=" + (sharePwd || "");
        } else {
            shareId = aliUrl.split("/")[4];
            return 'smartdrive://share/browse?shareId=' + shareId + '&sharePwd=' + (sharePwd || "");
        }
    } else if (aliUrl.startsWith("http")) {
        return "web://" + aliUrl;
    } else {
        return aliUrl;
    }
}

function getModeUrl(aliUrl, sharePwd, openMode) {
    if (aliUrl.includes("www.aliyundrive.com/s/")) {
        if (openMode === "本地转码") {
            return 'hiker://page/aliyun?page=fypage&rule=' + MY_RULE.title + '&realurl=' + encodeURIComponent(aliUrl) + "&sharePwd=" + (sharePwd || "");
        } else {
            shareId = aliUrl.split("/")[4];
            return 'smartdrive://share/browse?shareId=' + shareId + '&sharePwd=' + (sharePwd || "");
        }
    } else if (aliUrl.startsWith("http")) {
        return "web://" + aliUrl;
    } else {
        return aliUrl;
    }
}

$.exports.carryRule = function(rule, s, page, searchMode, openMode) {
    let list = rule.find(s, page);
    if (!list) return [];
    let urlLazy = $("").lazyRule(doubleTreating, openMode, rule.findAliUrl, MY_RULE.title);
    let descTips = "““””" + rule.name.fontcolor("#f13b66a") + "&nbsp;";
    let resList = [];

    for (let it of list) {
        if (it.skip) {
            resList.push(it);
            continue;
        }
        if (searchMode && !searchContains(it.title, s, true)) continue;
        it.url = rule.findAliUrl ? it.url + urlLazy : getModeUrl(it.url, it.sharePwd || "", openMode);
        resList.push({
            title: it.title.includes("““””") ? it.title : "““””" + it.title.replace(new RegExp("<em>|</em>", "g"), "").replace(new RegExp(s, "g"), "<strong><span style=\"color: #1E90FF\">" + s + "</span></strong>"),
            desc: descTips + (it.desc||""),
            url: it.url,
            pic_url: it.pic_url,
            col_type: it.pic_url ? 'movie_1_vertical_pic' : "text_1",
            extra: Object.assign({
                inheritTitle: false,
                noPic: true
            }, it.extra)
        });
    }
    return resList;
}
let size = 10;

let rules = $.require("hiker://page/rules");
let forbidden = storage0.getItem("forbidden", []);
rules=rules.filter(v => !forbidden.includes(v.name));

$.exports.getData = function(page, rule) {
    if (!page && !rule) {
        return rules.map(it => it.name);
    }
    if (rule != null) {
        if (page != null && page > 1) {
            return rules.filter(i => i.name == rule && i.page);
        }
        return rules.filter(i => i.name == rule);
    }
    let start = (page - 1) * size;
    if (rules.length < start + 1) {
        return [];
    }
    let len = size;
    if (rules.length < page * size) {
        len = rules.length - start
    }
    return rules.slice(start, start + len);
}

