function setClip() {
    function getCopy() {
        const Context = android.content.Context;
        const context = com.example.hikerview.ui.Application().getContext();
        // 获取系统剪贴板
        let clipboard = context.getSystemService(Context.CLIPBOARD_SERVICE);
        // 返回数据
        let clipData = clipboard.getPrimaryClip();
        if (clipData != null && clipData.getItemCount() > 0) {
            // 从数据集中获取（粘贴）第一条文本数据
            return String(clipData.getItemAt(0).getText().toString());
        }
        return null;
    }
    let text = getCopy() || "";
    let url = text.match(/http.*?www.aliyundrive.com\/s\/[a-zA-Z0-9]{11}(\/folder\/[a-z0-9]{40})?/);
    url = Array.isArray(url) && url[0];
    if (url) {
        let cache = getItem("clipboardCache", "");
        if (url !== cache) {
            d.push({
                title: '““””<font color="#13B61B">▐ </font><b>温馨提示<b>',
                desc: "检测到剪贴板含有阿里链接是否打开?\n" + url,
                url: $("#noLoading#").lazyRule((input, id) => {
                    deleteItem(id + "clipboard");
                    return "hiker://page/aliyun?pageTitle=打开链接&page=fypage&realurl=" + encodeURIComponent(input);
                }, url, id),
                col_type: "text_1",
                extra: {
                    id: id + "clipboard"
                }
            });
            setItem("clipboardCache", url);
        }
    }
}

