function intercept(rule, origin, openMode) {
    let host = origin.replace(new RegExp("http://|https://"), "").split("/")[0];
    let hosts = host.split(".");
    if (hosts.length > 2) {
        host = hosts[hosts.length - 2] + "." + hosts[hosts.length - 1];
    }
    if (input.startsWith("magnet")) {
        confirm({
            title: "温馨提示",
            content: "检测到新的磁链地址，点击确定按钮即可播放，点击取消按钮可以复制链接",
            confirm: $.toString((mag) => {
                return mag;
            }, input),
            cancel: $.toString((mag) => {
                return "copy://" + mag
            }, input)
        });
        return true;
    } else if (input.includes("aliyundrive.com") && openMode != "官方APP") {
        log(input);
        if (input.includes('checkurl?url=') && input.includes('&urlrefer')) {
            input = decodeURIComponent(input.split('&urlrefer')[0].split('checkurl?url=')[1]);
            //log('贴吧'+input);
        }
        return $.toString((url, rule) => {
            fy_bridge_app.open(JSON.stringify({
                title: "阿里云盘",
                url: "hiker://page/aliyun?page=fypage&realurl=" + encodeURIComponent(url) + "&rule=" + rule,
            }))
        }, input, rule);
    } else if (/\.(mp3|flac|m4a)$/.test(input)) {
        log(input);
        return $.toString((url, rule) => {
            fy_bridge_app.playVideo(url);
        }, input, rule);
    } else if (!input.includes(host) && input.startsWith("http")) {
        log(input);
        return $.toString((url, rule) => {
            fy_bridge_app.open(JSON.stringify({
                title: "详情",
                url: "hiker://page/ysfx?webUrl="+encodeURIComponent(url)+"&rule="+rule,
            }))
        }, input, rule);
    } else if (!input.startsWith("http")) {
        log(input);
        //能唤起APP的白名单
        let white = [
            "qklink",
            "bdnetdisk",
            "xunleiapp",
            "smartdrive"
        ];
        let inWhite = white.filter(it => input.startsWith(it)).length > 0;
        if (!inWhite) {
            return false;
        }
        return $.toString((url, rule) => {
            fy_bridge_app.openThirdApp && fy_bridge_app.openThirdApp(url);
        }, input, rule);
    }
}

