const AliAccessApi = $.require("aliPlayApi");
const {
    openTokensFilepath,
    aliTokensFilepath
} = $.require("tokenPath");

function aliM3u8Proxy(fileId, shareId, sharePwd, shareToken, aliToken, title) {
    function getAliPlayUrl(fileId, line) {
        let aliurl;
        try {
            const AliAccessApi = $.require("hiker://page/aliPlayApi?rule=" + title);
            let aliAccessApi = AliAccessApi.createDefault(shareId, sharePwd, shareToken, aliToken, null, title);
            let playUrlList = aliAccessApi.getShareVideoPreview(fileId) || [];
            playUrlList.forEach((item) => {
                if (item.template_id == line) {
                    aliurl = item.url;
                }
            });
        } catch (e) {
            log(e.toString());
            throw e;
        }
        return aliurl;
    }

    function getM3u8Content(playurl) {

        //log("我在代理" + playurl);
        let home = playurl.split('media.m3u8')[0];
        let f = fetch(playurl, {
            headers: {
                'Referer': 'https://www.aliyundrive.com/'
            },
            timeout: 3000
        }).split("\n");
        let ff = f.map(it => {
            if (it.startsWith("media-")) {
                return "/proxy?url=" + base64Encode(home + it);
            }
            return it;
        }).join("\n");
        //log('ufid-' + fileid);
        writeFile('hiker://files/_cache/' + fileId + '.m3u8', ff);
        return ff;
    }

    function updateTsUrl(url) {
        let fid = url.split('&f=')[1].split('&')[0];
        //log('sfid-' + fid);
        let f = fetch('hiker://files/_cache/' + fid + '.m3u8').split("\n");
        f.forEach(it => {
            if (it && it.startsWith('/proxy?url=')) {
                let furl = base64Decode(it.replace('/proxy?url=', ''));
                if (url.substr(url.indexOf('/media-'), url.indexOf('.ts')) == furl.substr(furl.indexOf('/media-'), furl.indexOf('.ts'))) {
                    url = furl;
                }
            }
        })
        let expires = url.split('x-oss-expires=')[1].split('&')[0];
        const lasttime = parseInt(expires) - Date.now() / 1000;
        if (lasttime < 60) {
            //log('过期更新')
            let line = url.split('/media')[0]; //取之前播放的ts段线路
            line = line.substring(line.lastIndexOf('/') + 1);
            let f = getM3u8Content(getAliPlayUrl(fid, line)).split("\n");
            f.forEach(it => {
                if (it && it.startsWith('/proxy?url=')) {
                    let furl = base64Decode(it.replace('/proxy?url=', ''));
                    if (url.substr(url.indexOf('/media-'), url.indexOf('.ts')) == furl.substr(furl.indexOf('/media-'), furl.indexOf('.ts'))) {
                        url = furl;
                    }
                }
            })

        }
        return url;
    }
    try {
        let url = base64Decode(MY_PARAMS.url);
        if (url.includes(".ts")) {
            return JSON.stringify({
                statusCode: 302,
                headers: {
                    "Location": updateTsUrl(url),
                    'Referer': 'https://www.aliyundrive.com/'
                }
            });
        } else {
            //log(url);
            let ff = getM3u8Content(getAliPlayUrl(fileId, MY_PARAMS.templateId));
            return ff;
        }
    } catch (e) {
        log("代理播放失败#\n错误信息>" + e.toString() + "\n错误行>" + e.lineNumber);
    }
}


function getVideo(aliAccessApi, fileId) {

    let playUrlList = aliAccessApi.getShareVideoPreview(fileId) || [];

    let serverAddress = startProxyServer($.toString(aliM3u8Proxy, fileId, aliAccessApi.shareId, aliAccessApi.sharePwd, aliAccessApi.shareToken, aliAccessApi.aliToken, MY_RULE.title));
    let transcoding = {
        UHD: "4K 超清",
        QHD: "2K 超清",
        FHD: "1080 全高清",
        HD: "720 高清",
        SD: "540 标清",
        LD: "360 流畅"
    };

    if (playUrlList.length > 0) {
        let urls = [],
            names = [];
        playUrlList.forEach((item) => {
            urls.push(serverAddress + "?url=" + base64Encode(item.url) + "&templateId=" + item.template_id + "#.m3u8");
            names.push(transcoding[item.template_id] ? transcoding[item.template_id] : item.template_height);
        });
        let heads = new Array(playUrlList.length).fill({
            'Referer': 'https://www.aliyundrive.com/'
        });
        return {
            urls: urls,
            names: names,
            headers: heads,
        };
    } else {
        return {};
    }

};


function getOriVideo(aliAccessApi, fileId) {
    return {
        names: ["原画"],
        urls: [aliAccessApi.getVideoTemPlayUrl(fileId) + "#isVideo=true#"],
        headers: [{
            'Referer': 'https://www.aliyundrive.com/'
        }]
    };
}
$.exports = function(shareId, sharePwd, shareToken, fileId, cate, zimu) {
    if (!fileExist(aliTokensFilepath)) {
        toast("请先登陆登录账号");
        return "hiker://page/login#noRecordHistory##noHistory#?noRefresh=1";
    }
    let aliToken = $.require(aliTokensFilepath);
    if (cate === "video") {
        let playList = [];
        let aliAccessApi;
        let playMode = getItem("aliyun_playMode", "转码");
        try {
            aliAccessApi = AliAccessApi.createDefault(shareId, sharePwd, shareToken, aliToken);
            if (playMode === "转码") {
                playList = getVideo(aliAccessApi, fileId) || {};
            } else {
                playList = getOriVideo(aliAccessApi, fileId);
            }
        } catch (e) {
            log("#" + playMode + "播放失败#\n错误信息>" + e.toString() + "\n错误行>" + e.lineNumber);
            if (e.code === "InvalidParameter.RefreshToken") {
                toast("登陆过期，请重新登陆");
                return "hiker://page/login#noRecordHistory##noHistory#?noRefresh=1";
            } else if (e.code === "QuotaExhausted.Drive") {
                return "toast://网盘空间不足，无法播放请清理。";
            }
            return "toast://播放失败，错误信息已打印。\n请查看日志(LOG)";
        }

        let mountsub = storage0.getMyVar('mountzimu', {});
        if (Object.keys(mountsub).length > 0) {
            zimu = mountsub;
        }
        let subtitle;
        if (Object.keys(zimu || {}).length > 0 && playList && playList.urls && playList.urls.length > 0) {
            java.lang.Thread.sleep(100);
            try {
                var zmurl = aliAccessApi.getFileToDownUrl(zimu.file_id);
                let path = 'hiker://files/cache/云盘君/sub.' + zimu.ext;
                downloadFile(zmurl, path, {
                    'Referer': 'https://www.aliyundrive.com/'
                });
                subtitle = getPath(path);
            } catch (e) {
                toast("字幕加载失败");
                log(e.message);
            }
        }
        let dmlist = storage0.getMyVar("dmlist", []);
        let dmlist_cache = getMyVar("dmlist_cache", "");
        if (dmlist_cache && dmlist_cache.startsWith(fileId + "#")) {
            playList.danmu = dmlist_cache.replace(fileId + "#", "");
        } else if (dmlist.length > 0) {
            playList.danmu = "hiker://files/cache/danmu/" + dmlist.splice(0, 1)[0];
            putMyVar("dmlist_cache", fileId + "#" + playList.danmu);
            storage0.putMyVar("dmlist", dmlist);
            if (dmlist.length === 0) {
                toast("弹幕列表已全部挂载");
            }
        }else{
            playList.danmu = getVar("dm_share","");
        }
        playList.subtitle = subtitle;
        return JSON.stringify(playList);
    }

    let aliAccessApi = AliAccessApi.createDefault(shareId, sharePwd, shareToken, aliToken, null);
    let url = aliAccessApi.getFileToDownUrl(fileId);

    if (cate === "image") {
        return url + "#.jpg";
    } else if (cate === "audio") {
        return url + "#isMusic=true#";
    } else {
        return "download://" + url;
    }
}

