//@author LoyDgIk
function AliError(errorCode, message, significance) {
    let errorType = AliError.errorCodeMap.get(errorCode) || [];
    this.code = errorCode;
    this.message = errorType[0] || message || "";
    this.name = "AliError";
    this.significance = errorType[1] || significance || 1;
}
AliError.errorCodeMap = new Map([
    ["ShareLink.Cancelled", ["分享链接已失效"]],
    ["ShareLink.Forbidden", ["违规资源已被封禁"]],
    ["NotFound.ShareLink", ["不存在该链接请核对"]],
    ["AccessTokenInvalid", ["访问令牌失效，请重新登陆"]],
    ["ShareLinkTokenInvalid", ["分享令牌失效"]],
    ["ParamFlowException", ["访问过于频繁，请稍后再试", 3]]
]);

AliError.prototype = Object.assign(new Error(), {
    constructor: AliError
});

function postApi(api, body, headers) {
    let result = request('https://api.aliyundrive.com' + api, {
        headers: Object.assign({
            'Content-Type': 'application/json',
        }, headers),
        body: JSON.stringify(body),
        method: 'POST'
    });
    result = JSON.parse(result);
    if (result.code) {
        throw new AliError(result.code, result.message);
    }
    return result;
}

function AliShareApi(shareId, sharePwd, shareToken, refreshCallBack) {
    this.shareId = shareId;
    this.sharePwd = sharePwd;
    this.refreshCallBack = refreshCallBack || (() => void 0);
    this.shareToken = shareToken || this.getShareToken();
}
Object.assign(AliShareApi.prototype, {
    getShareToken() {
        let result = postApi("/v2/share_link/get_share_token", {
            "share_id": this.shareId,
            "share_pwd": this.sharePwd,
        });
        this.shareToken = result.share_token;
        this.refreshCallBack(result.share_token, this);
        return result.share_token;
    },
    postTokenApi(api, body, headers, isRetry) {
        let result;
        try {
            result = postApi(api, body, Object.assign({
                "X-Share-Token": this.shareToken || this.getShareToken()
            }, headers));
        } catch (e) {
            if ((e.code === "AccessTokenInvalid" || e.code === "ShareLinkTokenInvalid") && !isRetry) {
                this.getShareToken();
                return this.postTokenApi(api, body, headers, true);
            }
            throw e;
        }
        return result;
    },
    getListFile(fileId, marker, orderBy, orderDirection) {
        let result = this.postTokenApi("/adrive/v3/file/list", {
            "share_id": this.shareId,
            "parent_file_id": fileId || "root",
            "limit": 100,
            "image_thumbnail_process": "image/resize,w_160/format,jpeg",
            "image_url_process": "image\/resize,w_1920/format,jpeg",
            "video_thumbnail_process": "video/snapshot,t_1000,f_jpg,ar_auto,w_300",
            "order_by": orderBy || "name",
            "order_direction": orderDirection || "ASC",
            "marker": marker
        });
        return result;
    },
    getShareInfo() {
        let result = postApi("/adrive/v3/share_link/get_share_by_anonymous?share_id=" + this.shareId, {
            "share_id": this.shareId
        });
        return result;
    },
    getSearchListFile(key, marker, orderBy, orderDirection) {
        let result = this.postTokenApi("/recommend/v1/shareLink/search", {
            "share_id": this.shareId,
            "keyword": key,
            "limit": 100,
            "order_by": orderBy + " " + orderDirection,
            "marker": marker || undefined
        });
        if (Object.keys(result).length === 0) {
            result.items = [];
        }
        return result;
    }
});
$.exports = AliShareApi;

//@author LoyDgIk
function postApi(api, body, headers, againTry, tryFre) {
    let result;
    try {
        result = request(api.startsWith("http") ? api : 'https://api.aliyundrive.com' + api, {
            headers: Object.assign({
                'Content-Type': 'application/json',
            }, headers),
            body: JSON.stringify(body),
            method: 'POST'
        });
        result = JSON.parse(result);
        if (result.code) {
            let msg = result.display_message || result.message;
            let error = new Error(api + "=>" + result.code + ":" + msg);
            error.code = result.code;
            error.judgeMsg = msg
            throw error;
        }
        let rbody = result && result.responses && result.responses[0] && result.responses[0].body || {};
        if (rbody.code) {
            let msg = rbody.display_message || rbody.message;
            let error = new Error(api + "=>" + rbody.code + ":" + msg);
            error.code = rbody.code;
            error.judgeMsg = msg;
            throw error;
        }
    } catch (e) {
        tryFre = tryFre === undefined ? 1 : tryFre;
        if (againTry && tryFre > 0) {
            if (typeof againTry === "function") {
                let newData = againTry(e, result, body, headers, tryFre);
                if (newData) {
                    Object.assign(body, newData.body);
                    Object.assign(headers, newData.headers);
                }
            }
            return postApi(api, body, headers, againTry, tryFre - 1);
        }
        throw e;
    }
    return result;
}

function AliAccessApi(shareId, sharePwd, shareToken, aliTokens, openTokens, refreshShareCallBack, refreshAccessCallBack, refreshOpenCallBack) {
    this.shareId = shareId;
    this.sharePwd = sharePwd;
    this.refreshShareCallBack = refreshShareCallBack || (() => void 0);
    this.refreshAccessCallBack = refreshAccessCallBack || (() => void 0);
    this.refreshOpenCallBack = refreshOpenCallBack || (() => void 0);
    this.shareToken = shareToken || this.getShareToken();
    this.aliTokens = aliTokens || {};
    if (!this.aliTokens.refresh_token) {
        throw new Error("未登陆");
    }
    if (!this.aliTokens.access_token) {
        this.refreshAccessToken();
    }
    this.defDriveId = this.aliTokens.resource_drive_id || this.aliTokens.default_drive_id;
    this.reaccessTk = (this.aliTokens.token_type || "Bearer") + ' ' + this.aliTokens.access_token;
    this.openTokens = openTokens || {
        'RefreshTokenOpen': '',
        'AccessTokenOpen': '',
        'BindRefreshToken': '',
    };
    if (openTokens !== null && !this.openTokens.AccessTokenOpen) {
        this.refreshOpenToken();
    }
}
Object.assign(AliAccessApi.prototype, {
    waitSleep(time) {
        java.lang.Thread.sleep(time || 600);
        return this;
    },
    trySolve(e) {
        if (e.code === "ShareLinkTokenInvalid" || e.code === "InvalidParameterNotMatch.ShareId") {
            this.waitSleep(20).getShareToken();
            return true;
        } else if (e.code === "AccessTokenInvalid" || e.code === "AccessTokenExpired") {
            this.waitSleep(20).refreshAccessToken();
            return true;
        } else if (e.code === "ForbiddenNoPermission.File" || (e.code === "BadRequest" && e.judgeMsg === "仅可保存到转存空间")) {
            this.aliTokens.resource_drive_id = undefined;
            this.waitSleep(20).refreshAccessToken();
            return true;
        }
        return false;
    },
    getShareToken() {
        let result = postApi("/v2/share_link/get_share_token", {
            "share_id": this.shareId,
            "share_pwd": this.sharePwd,
        });
        this.shareToken = result.share_token;
        this.refreshShareCallBack(result.share_token, this);
        return result.share_token;
    },
    refreshAccessToken() {
        let result = postApi("https://auth.aliyundrive.com/v2/account/token", {
            "grant_type": "refresh_token",
            "refresh_token": this.aliTokens.refresh_token,
        }, {
            "User-Agent": PC_UA,
            "Referer": "https://www.aliyundrive.com/"
        }, (e) => {
            if (e.code === "InvalidParameter.RefreshToken") {
                e.message = "#登陆过期请重新登陆#\n" + e.message;
                throw e;
            }
        });
        delete result.user_data;
        let originDefdDriveId = this.aliTokens.default_drive_id;
        this.aliTokens = Object.assign(this.aliTokens, result);
        this.reaccessTk = this.aliTokens.token_type + ' ' + this.aliTokens.access_token;
        try {
            if ((originDefdDriveId !== this.aliTokens.default_drive_id) || this.aliTokens.resource_drive_id === undefined) {
                let resource_drive_id = this.getUserInfo().resource_drive_id;
                this.aliTokens.resource_drive_id = resource_drive_id;
                if (resource_drive_id) {
                    this.defDriveId = resource_drive_id;
                } else {
                    this.defDriveId = this.aliTokens.default_drive_id;
                }
            }
        } catch (e) {
            log("用户信息获取失败：" + e.toString());
        }
        this.refreshAccessCallBack(result, this);
        return this.aliTokens;
    },
    getUserInfo() {
        let info = postApi("https://user.aliyundrive.com/v2/user/get", {}, {
            "User-Agent": PC_UA,
            "authorization": this.reaccessTk,
            "Referer": "https://www.aliyundrive.com/"
        }, (e) => {
            this.trySolve(e);
            return {
                headers: {
                    'authorization': this.reaccessTk
                }
            }
        });
        return info;
    },
    _getOpenToken(code) {
        let body = {},
            headers = {
                'User-Agent': PC_UA,
                'Referer': 'https://www.aliyundrive.com/'
            };
        if (code) {
            body = {
                code: code,
                grant_type: "authorization_code"
            };
        } else {
            body = {
                grant_type: "refresh_token",
                refresh_token: this.openTokens.RefreshTokenOpen,
            }
        }
        let json = post('https://api.xhofe.top/alist/ali_open/code', {
            headers: headers,
            body: JSON.stringify(body),
        });
        if (!json) {
            json = post('https://api-cf.nn.ci/alist/ali_open/code', {
                headers: headers,
                body: JSON.stringify(body),
            });
        }
        if (json.includes('Too Many Requests') || json.includes("502 Bad Gateway")) {
            throw new Error("频繁请求暂时被封禁\n" + json);
        } else {
            let openResult = JSON.parse(json);
            if (openResult.refresh_token) {
                this.openTokens.RefreshTokenOpen = openResult.refresh_token;
                this.openTokens.AccessTokenOpen = openResult.token_type + " " + openResult.access_token;
                this.openTokens.BindRefreshToken = this.aliTokens.refresh_token;
                this.refreshOpenCallBack(openResult, this);
                return this.openTokens;
            }
        }
        if (!code) {
            return this.waitSleep(50).cacheOpenToken();
        }
        throw new Error("openToken获取失败\n" + json);
    },
    cacheOpenToken() {
        let result = postApi('https://open.aliyundrive.com/oauth/users/authorize?client_id=' + '76917ccccd4441c39457a04f6084fb2f' + '&redirect_uri=https://alist.nn.ci/tool/aliyundrive/callback&scope=user:base,file:all:read,file:all:write&state=', {
            "authorize": 1,
            "scope": "user:base,file:all:read,file:all:write"
        }, {
            'User-Agent': PC_UA,
            'Referer': 'https://www.aliyundrive.com/',
            'authorization': this.reaccessTk, //getAccessToken
            'x-canary': 'client=web,app=share,version=v2.3.1',
            'x-share-token': this.shareToken
        }, (e) => {
            this.trySolve(e);
            return {
                headers: {
                    'authorization': this.reaccessTk,
                    'x-share-token': this.shareToken
                }
            }
        });
        let code = result.redirectUri.split("code=")[1];
        return this._getOpenToken(code);
    },
    refreshOpenToken() {
        if (this.openTokens.RefreshTokenOpen && this.openTokens.BindRefreshToken === this.aliTokens.refresh_token) {
            return this._getOpenToken();
        } else {
            return this.cacheOpenToken();
        }
    },
    _cloudFileOperate(body, cloudFileId, operateUrl) {
        let result = postApi('https://api.aliyundrive.com/adrive/v2/batch', {
            "requests": [{
                "body": body,
                "headers": {
                    "Content-Type": "application/json"
                },
                "id": cloudFileId || "0",
                "method": "POST",
                "url": operateUrl
            }],
            "resource": "file"
        }, {
            'User-Agent': PC_UA,
            'Referer': 'https://www.aliyundrive.com/',
            'authorization': this.reaccessTk,
            //getAccessToken
            'x-canary': 'client=web,app=share,version=v2.3.1',
            'x-share-token': this.shareToken

        }, (e, r, b) => {
            if (e.code === "QuotaExhausted.Drive") {
                throw e;
            }
            if (!this.trySolve(e)) {
                this.waitSleep(20).refreshAccessToken();
            }
            let body = b.requests[0].body;
            if (body.drive_id) {
                body.drive_id = this.defDriveId
            }
            if (body.to_drive_id) {
                body.to_drive_id = this.defDriveId
            }
            return {
                body: b,
                headers: {
                    'authorization': this.reaccessTk,
                    'x-share-token': this.shareToken
                }
            };
        });
        return result;
    },
    copyFileToCloudFile(fileId, retry) {
        let result = this._cloudFileOperate({
            "file_id": fileId,
            "share_id": this.shareId,
            "auto_rename": true,
            "to_parent_file_id": "root",
            "to_drive_id": this.defDriveId
        }, "0", "/file/copy");
        let body = result && result.responses && result.responses[0] && result.responses[0].body || {};
        if (!body.file_id && !retry) {
            this.waitSleep(20).getShareToken();
            this.waitSleep(20).refreshAccessToken();
            return this.waitSleep(20).copyFileToCloudFile(fileId, true);
        } else if (!body.file_id) {
            let e = new Error("文件复制失败:\n" + JSON.stringify(result));
            e.code = body.code;
            throw e;
        }
        return result.responses[0].body.file_id;
    },
    openCloudFileToDownUrl(cloudFileId) {
        let fileInfo = postApi('https://open.aliyundrive.com/adrive/v1.0/openFile/getDownloadUrl', {
            'file_id': cloudFileId,
            'drive_id': this.defDriveId
        }, {
            'User-Agent': PC_UA,
            'Referer': 'https://www.aliyundrive.com/',
            'authorization': this.openTokens.AccessTokenOpen
        }, (e) => {
            this.waitSleep(30).trySolve(e);
            this.waitSleep(30).refreshOpenToken();
            return {
                body: {
                    'drive_id': this.defDriveId,
                },
                headers: {
                    'authorization': this.openTokens.AccessTokenOpen
                }
            };
        }, 2);
        if (!fileInfo.url || fileInfo.code) throw new Error("下载链接获取失败\n" + fileInfo.code + ":" + fileInfo.message);
        return fileInfo.url;
    },
    deleteeCloudFile(cloudFileId) {
        let result = this._cloudFileOperate({
            "drive_id": this.defDriveId,
            "file_id": cloudFileId
        }, cloudFileId, "/file/delete");
        return result;
    },
    getVideoTemPlayUrl(fileId) {
        let cloudFileId = this.copyFileToCloudFile(fileId);
        this.waitSleep(20);
        let playUrl = this.openCloudFileToDownUrl(cloudFileId);
        this.waitSleep(50);
        try {
            this.deleteeCloudFile(cloudFileId);
        } catch (e) {
            log("#文件删除失败#\n错误信息>" + e.toString() + "\n错误行>" + e.lineNumber);
        }
        return playUrl;
    },
    getFileToDownUrl(fileId) {
        let downloadInfo = postApi("/v2/file/get_share_link_download_url", {
            "expire_sec": 600,
            "file_id": fileId,
            "share_id": this.shareId
        }, {
            "X-Share-Token": this.shareToken,
            "authorization": this.aliTokens.access_token
        }, (e) => {
            this.trySolve(e);
            return {
                headers: {
                    "X-Share-Token": this.shareToken,
                    "authorization": this.aliTokens.access_token
                }
            };
        });
        return downloadInfo.download_url;
    },
    getSign() {
        if (!this.aliTokens.device_id) {
            this.waitSleep(20).refreshAccessToken();
        }
        let calculateNewSign = () => {
            let sign = justTestSign("5dde4e1bdf9e4966b387ba58f4b3fdc3", this.aliTokens.device_id, this.aliTokens.user_id).split("##");
            return {
                signature: sign.at(0),
                pubKey: sign.at(-1)
            };
        };
        let newSign = calculateNewSign();
        let result = postApi('/users/v1/users/device/create_session', {
            "deviceName": "Edge浏览器",
            "modelName": "Windows网页版",
            "pubKey": newSign.pubKey,
        }, {
            'User-Agent': PC_UA,
            'Referer': 'https://www.aliyundrive.com',
            'Authorization': this.reaccessTk,
            'X-Share-Token': this.shareToken,
            'x-device-id': this.aliTokens.device_id,
            'x-signature': newSign.signature
        }, (e) => {
            if (e.message === "x-device-id empty") {
                this.waitSleep(20).refreshAccessToken();
            }
            this.trySolve(e);
            this.waitSleep(50);
            newSign = calculateNewSign();
            return {
                body: {
                    "pubKey": newSign.pubKey
                },
                headers: {
                    'Authorization': this.reaccessTk,
                    'X-Share-Token': this.shareToken,
                    'x-device-id': this.aliTokens.device_id,
                    'x-signature': newSign.signature
                }
            }
        }, 3);
        if (result.success) {
            return newSign;
        } else {
            throw new Error("Sign获取失败，请重试。\ncode:" + (result.code || "") + "\nmessage:" + (result.message || ""));
        }
    },
    getShareVideoPreview(fileId) {
        let sign = this.getSign();
        let json = JSON.parse(request('https://api.aliyundrive.com/v2/file/get_share_link_video_preview_play_info', {
            headers: {
                'content-type': 'application/json;charset=UTF-8',
                "origin": "https://www.aliyundrive.com",
                "referer": "https://www.aliyundrive.com/",
                "user-agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/110.0.0.0 Safari/537.36 Edg/110.0.1587.41",
                "x-canary": "client=web,app=adrive,version=v3.1.0",
                "x-share-token": this.shareToken,
                "fileid": this.aliTokens.user_id,
                'Authorization': this.reaccessTk,
                'x-device-id': this.aliTokens.device_id,
                'x-signature': sign.signature
            },
            body: {
                "category": "live_transcoding",
                "file_id": fileId,
                "get_preview_url": true,
                "share_id": this.shareId,
                "template_id": "",
                "get_subtitle_info": true
            },
            method: 'POST',
            timeout: 3000
        }));
        return json.video_preview_play_info.live_transcoding_task_list.reverse();
    }
});

AliAccessApi.createDefault = function(shareId, sharePwd, shareToken, aliToken, openToken, MY_TITLE) {
    const {
        openTokensFilepath,
        aliTokensFilepath
    } = $.require("tokenPath" + (MY_TITLE ? "?rule=" + MY_TITLE : ""));

    function saveAliToken(result, api) {
        writeFile(aliTokensFilepath, JSON.stringify(api.aliTokens));
    }

    function saveOpenToken(result, api) {
        writeFile(openTokensFilepath, JSON.stringify(api.openTokens));
    }

    function putShareToken(result) {
        if (MY_TITLE) {
            shareToken = putVar(MY_TITLE + "@" + "aliShareToken", result);
        } else {
            shareToken = putMyVar("aliShareToken", result);
        }
    }
    if (!openToken && openToken !== null && fileExist(openTokensFilepath)) {
        openToken = $.require(openTokensFilepath);
    }
    if (!aliToken && fileExist(aliTokensFilepath)) {
        aliToken = $.require(aliTokensFilepath);
    }
    if (MY_TITLE) {
        shareToken = getVar(MY_TITLE + "@" + "aliShareToken", shareToken);
    } else {
        shareToken = getMyVar("aliShareToken", shareToken);
    }
    return new AliAccessApi(shareId, sharePwd, shareToken, aliToken, openToken, putShareToken, saveAliToken, saveOpenToken);
}
