function search(d, name) {
    const danmu = $.require("danmu");
    d.push({
        col_type: "line"
    });
    d.push({
        col_type: "line"
    });
    d.push({
        col_type: "line"
    });
    let dmList = danmu.getDanMu(name);

    for (let anime of dmList.animes) {
        d.push({
            title: anime.animeTitle,
            desc: anime.typeDescription,
            col_type: "avatar",
            url: $("", "请输入下载范围(1," + anime.episodes.length + ")").input((animeTitle, episodes) => {
                let [a, b] = input.split(/,|，/);
                a = Number(a), b = Number(b);
                if (Number.isNaN(a) || Number.isNaN(b)) {
                    return "toast://请按格式输入数字";
                }
                if (a < 1 || b > episodes.length) {
                    return "toast://请输入规定范围内的数字";
                }
                const danmu = $.require("danmu");
                try {
                    for (let i = a - 1, j = 1; i < b; i++, j++) {
                        let episode = episodes[i];
                        let dmTitle = animeTitle + "_" + episode.episodeTitle;
                        showLoading("正在下载：" + episode.episodeTitle + "\t(" + j + "/" + (b - a + 1) + ")");
                        danmu.downloadDanMu(episode.episodeId, dmTitle);
                    }
                } catch (e) {
                    log(e.toString());
                    hideLoading();
                    return "toast://哦哦下载失败...";
                }
                hideLoading();
                return "toast://下载完成";
            }, anime.animeTitle, anime.episodes),
            pic_url: "https://hikerfans.com/tubiao/movie/14.svg"
        });
        d.push({
            col_type: "line"
        });
        for (let episode of anime.episodes) {
            let dmTitle = anime.animeTitle + "_" + episode.episodeTitle;
            d.push({
                title: episode.episodeTitle,
                url: $(episode.episodeId).lazyRule((url, title, onlyDownload) => {
                    const danmu = $.require("danmu");
                    let path = danmu.getLocalDanMu(input, title) || danmu.downloadDanMu(input, title);
                    if (onlyDownload) return "toast://下载完成";
                    let danmuText = fetch(path);
                    if (!danmuText) return "toast://还没有弹幕哟~";
                    refreshVideoUrl({
                        danmu: path
                    });
                    back(false);
                    return "toast://加载完成";
                }, MY_PARAMS.durl, dmTitle, onlyDownload),
                col_type: "text_1",
                extra: {
                    longClick: [{
                        title: "强制下载",
                        js: $.toString((episodeTitle, episodeId) => {
                            showLoading("请等待");
                            const danmu = $.require("danmu");
                            danmu.downloadDanMu(episodeId, episodeTitle);
                            hideLoading();
                            return "toast://重新下载完成，单击挂载。";
                        }, dmTitle, episode.episodeId)
                    }]
                }
            });
        }
        d.push({
            col_type: "line_blank"
        });
    }
    if (!dmList.animes.length) {
        d.push({
            title: "没有找到与[" + name + "]相关的弹幕",
            url: "hiker://empty",
            desc: "搜不到怎么办？\n检查名称是否正确；可以尝试删除部分限定词，如第一季等；而且只能搜索番剧哦！其他弹幕可用dm盒子。",
            col_type: "text_center_1"
        });
    }
}
let name = getParam("name", MY_PARAMS.name);
if (name) {
    try {
        search(d, name);
    } catch (e) {
        d.push({
            title: "““ERROR””",
            desc: e.toString(),
            col_type: "text_1"
        });
    }
}
setResult(d);

