                });
            }
            return d;
        }
    }, {
        name: "资源帮",
        page: true,
        find(s, page) {
            function search() {
                let rtnList = [];
                let resp = request("https://zyb.upyunso.com/v15/search?keyword=" + en_aes(s) + "&s_type=2&page=" + page + "&atk=&did=" + generateRandomHexString(64), {
                    headers: {
                        "user-agent": "Mozilla/5.0 (Linux; Android 10; Redmi Note 7 Build/QKQ1.190910.002; wv) AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/81.0.4044.138 Mobile Safari/537.36 uni-app Html5Plus/1.0 (Immersed/29.09091)",
                    },
                });
                let respData = de_aes(resp);
                let jsonData = JSON.parse(respData) || {};
                let result = jsonData.result || {};
                let items = result.items || [];

                for (let i = 0; i < items.length; i++) {
                    let item = items[i];
                    let content = item.content || [];
                    if (content.length == 0||!item.page_url) continue;
                    let url = de_aes(item.page_url) || "";
                    if (!url) continue;
                    rtnList.push({
                        title: (item.title.length>30?item.title.slice(0,27)+"...":item.title) || "",
                        desc: "分享时间："+item.insert_time,
                        url: url
                    });
                }

                return rtnList;
            }

            function generateRandomHexString(length) {
                let result = "";
                const characters = "0123456789abcdef";
                const charactersLength = characters.length;
                for (let i = 0; i < length; i++) {
                    result += characters.charAt(Math.floor(Math.random() * charactersLength));
                }
                return result;
            }
            let aliUtil = $.require("aliUtil");
            let CryptoJS = aliUtil.getCryptoJS();

            function en_aes(e) {
                let i = "qq1920520460qqzz";
                // en_aes
                let key = CryptoJS.enc.Utf8.parse(i);
                var t = CryptoJS.enc.Utf8.parse(e),
                    r = CryptoJS.AES.encrypt(t, key, {
                        iv: key,
                        mode: CryptoJS.mode.CBC,
                    });
                return r.ciphertext.toString();
            }

            function de_aes(vv) {
                let i = "qq1920520460qqzz";
                // de_aes
                let key = CryptoJS.enc.Utf8.parse(i);
                let hexVal = CryptoJS.enc.Hex.parse(vv),
                    val = CryptoJS.enc.Base64.stringify(hexVal),
                    decrypted = CryptoJS.AES.decrypt(val, key, {
                        iv: key,
                        mode: CryptoJS.mode.CBC,
                    });
                return decrypted.toString(CryptoJS.enc.Utf8);
            }
            return search();
        }
    }, {
