function encodeHtml(s) {
    let REGX_HTML_ENCODE = /“|&|’|\u003C|>|[\x00-\x20]|[\x7F-\xFF]|[\u0100-\u2700]/g;
    return s.replace(REGX_HTML_ENCODE, function($0) {
        var c = $0.charCodeAt(0),
            r = ["&#"];
        c = (c == 0x20) ? 0xA0 : c;
        r.push(c);
        r.push(";");
        return r.join("");
    });
}



