let relyfile;
let cfgfile = "hiker://files/rules/Src/Hk/config.json";
if (fileExist(cfgfile)) {
    eval("let Juconfig=" + fetch(cfgfile) + ";");
    relyfile = Juconfig["依赖"];
}
require(relyfile);
let name = getParam("keyword");
let type = getParam("type");
newsousuopage(name,type,relyfile);

function Lazy(input) {
    function GetDm(name, i) {
        var title = "第" + (parseInt(i) + 1) + "集"
        var danmu = $.require("hiker://page/danmu");
        let dmList = danmu.getDanMu(encodeURIComponent(name));

        var anime = dmList.animes;
        if (anime.length > 0) {
            var episode = anime[0].episodes
            if (episode.length - 1 >= i) {
                var episodeId = episode[i].episodeId;
                var episodeTitle = episode[i].episodeTitle;

                try {
                    let path = danmu.getLocalDanMu(episodeId, name + "_" + title) || danmu.downloadDanMu(episodeId, name + "_" + title);

                    if (path == undefined) {
                        toast("暂无弹幕")
                        return ""

                    } else {
                        toast("弹幕加载成功")

                        return path
                    }
                } catch (e) {
                    toast("弹幕加载失败")
                    return ""
                }
            }
        }

        toast("暂无弹幕")
        return ""
    }
    let url = input.split("##")[1];
    var find = findItem(url);
    var linkid = find.extra.linkid;
    var name = find.extra.name;
    let 公共 = {};
    try {
        公共 = $.require('jiekou' + (/搜视/.test(参数.规则名) ? '' : '?rule=搜视')).公共(参数.标识);

    } catch (e) {
        toast('未找到搜视规则子页面');
    }


    if (/qq.com|iqiyi|youku|bilibili|mgtv|pptv/.test(url)) {

        eval("let 解析2 = " + 解析);
        return 解析2(url, 公共, 参数);

    } else if (/.torrent|ed2k:|magnet:|m3u8/.test(url)) {
        return url
    } else if (getItem("superweb", "0") == "1") {
        var video = "video://" + url
    } else if (getItem("parsemode") == "1") {
        if (公共 && 公共.lazy) {
            var video = 公共.lazy(url)
        } else {
            eval("let 解析2 = " + 解析);
            var video = 解析2(url, 公共, 参数);
        }
    } else {
        require(config.依赖.match(/http(s)?:\/\/.*\//)[0].replace("/Ju/", "/master/") + "SrcParseS.js");
        return SrcParseS.task({}, url);
    }
    let dmlist = storage0.getMyVar("dmlist", []);
    let dmlist_cache = getMyVar("dmlist_cache", "");
    if (dmlist_cache && dmlist_cache.startsWith(url+ "#")) {
        danmu = dmlist_cache.replace(url + "#", "");
    } else if (dmlist.length > 0) {
        danmu = "hiker://files/cache/danmu/" + dmlist.splice(0, 1)[0];
        putMyVar("dmlist_cache", url+ "#" + danmu);
        storage0.putMyVar("dmlist", dmlist);
        if (dmlist.length === 0) {
            toast("弹幕列表已全部挂载");
        }
    } else if (getItem("dm") == "1" && !/x5WebView/.test(video)) {
        danmu = GetDm(name, linkid)

    }else {
     danmu=getVar(getPageTitle() ,"");
     clearVar(getPageTitle())
    }


    if (danmu) {

        try {
            var urls = JSON.parse(video)
            urls.danmu = danmu;
            return urls;

        } catch (e) {
            return JSON.stringify({
                urls: [video],
                danmu: danmu
            });
        }
    } else {
        return video
    }
}

