let d = [];
addListener('onClose', () => {
clearMyVar("s")
})
var key=MY_PARAMS.name || getMyVar("s") || decodeURIComponent(getParam("title", "")) || "";
d.push({
    col_type: "text_1",
    title: "““””<font color='Gray'>❗搜索““"+key+"””</font>",
    url: $(key, "请搜索番剧名称").input(() => {
        setPageParams({
            name: input
        });

        refreshPage();

    }),
});
d.push({
    col_type: "line"
});

d.push({
    title: "内置搜索弹幕",
    url: $("").lazyRule((key) => {
        /*setPageParams({
            name: input
        });*/

        putMyVar("s",key)
        refreshPage();
        return "toast://搜索："+key
      },key),
    col_type: "text_2"
});

d.push({
    title: "dm盒子搜索弹幕",
    url: $("#noLoading#").lazyRule((key) => {

            return  "hiker://search?rule=dm盒子&s=" + key;


    }, key),
    col_type: "text_2"
});

function search(d, name) {
    const danmu = $.require("danmu");
    d.push({
        col_type: "line"
    });
    d.push({
        col_type: "line"
    });
    d.push({
        col_type: "line"
    });
    let dmList = danmu.getDanMu(name);

    for (let anime of dmList.animes) {
        d.push({
            title: anime.animeTitle,
            desc: anime.typeDescription,
            col_type: "avatar",
            url: "hiker://empty",
            pic_url: "https://hikerfans.com/tubiao/movie/14.svg"
        });
        d.push({
            col_type: "line"
        });
        for (let episode of anime.episodes) {
            d.push({
                title: episode.episodeTitle,
                url: $(episode.episodeId).lazyRule((url, title) => {
                    const danmu = $.require("danmu");
                    let path = danmu.getLocalDanMu(input, title) || danmu.downloadDanMu(input, title);
                    let danmuText = fetch(path);
                    if (!danmuText) return "toast://还没有弹幕哟~";
                    refreshVideoUrl({
                        danmu: path
                    });
                    putVar("dm_share",path);
                    back(false);
                    return "toast://加载完成";
                }, MY_PARAMS.durl, anime.animeTitle + "_" + episode.episodeTitle),
                col_type: "text_1",
                extra: {
                    longClick: [{
                        title: "强制下载",
                        js: $.toString((episodeTitle, episodeId) => {
                            showLoading("请等待");
                            const danmu = $.require("danmu");
                            danmu.downloadDanMu(episodeId, episodeTitle);
                            hideLoading();
                            return "toast://重新下载完成，单击挂载。";
                        }, anime.animeTitle + "_" +episode.episodeTitle, episode.episodeId)
                    }]
                }
            });
        }
        d.push({
            col_type: "line_blank"
        });
    }
    if (!dmList.animes.length) {
        d.push({
            title: "没有找到与[" + name + "]相关的弹幕",
            url: "hiker://empty",
            desc: "搜不到怎么办？\n检查名称是否正确；可以尝试删除部分限定词，如第一季等；而且只能搜索番剧哦！其他弹幕可用dm盒子。",
            col_type: "text_center_1"
        });
    }
}
//let name = getParam("name", MY_PARAMS.name);
let name = getMyVar("s")
if (name) {
    try {
        search(d, name);
    } catch (e) {
        d.push({
            title: "““ERROR””",
            desc: e.toString(),
            col_type: "text_1"
        });
    }
}
setResult(d);

