js:
// 数据定位
var d = [];
if (MY_PARAMS.season_id) {
    season_id = MY_PARAMS.season_id + "";
    Data = JSON.parse(fetch('https://api.bilibili.com/pgc/view/web/season?season_id=' + season_id)).result;
} else {
    Data = JSON.parse(fetch('https://api.bilibili.com/pgc/view/web/season?ep_id=' + MY_PARAMS.ep_id)).result;
}
if (!Data.cover.match(/https/)) {
    封面 = Data.cover.replace('http','https') + "@Referer=";
}else{
    封面 = Data.cover+ "@Referer=";
}
try {
    地区 = Data.areas[0].name;
} catch (e) {
    地区='地区暂无';
}
简介 = Data.evaluate;
// 正片
类型 = Data.positive.title;
上映时间 = Data.publish.pub_time;
try {
    得分 = Data.rating.score;
} catch (e) {
    得分 = '暂无得';
}
追剧日历 = Data.new_ep.desc;
更新进度 = Data.new_ep.title;
影片名称 = Data.title;
第二标题 = Data.share_sub_title;
剧集列表 = Data.episodes;
短讯 = "⭐" + 类型 + "⭐" + 第二标题 + '\n' + 地区 + '-' + 得分 + '分\n-' + 上映时间 + '-';
// 重定义二级页面数据
setPageTitle(影片名称);
setPagePicUrl(封面);
// 前置参数和预处理---
imgLink = storage0.getMyVar('图标');
// 全局lazy——直链+断插
var lazy = $('').lazyRule(() => {
    videoParms = findItem(input);
    aid = videoParms.extra.aid;
    bvid = videoParms.extra.bvid;
    cid = videoParms.extra.cid;
    badge = videoParms.extra.badge;
    // 提交历史记录
    $.require("hiker://page/api?rule=哔哩K").reportProcess(aid, cid);
    vipStatus=JSON.parse(request("hiker://files/rules/cy/bili.txt")).vipStatus;
    if (vipStatus==1) {
        // 大会员
        if (badge.match(/付费/)) {
            return $(['魔断解析','已购买'], 1).select((input,aid,cid) => {
                if (input=='魔断解析') {
                    eval(fetch('hiker://files/cache/Parse_Dn.js', {}));
                    return aytmParse(link);
                }else{
                    return $.require("hiker://page/api").biliLazy(aid, cid, 116).replace(/;/g, "；；");
                }
            },link,aid,cid)
        } else {
            return $.require("hiker://page/api").biliLazy(aid, cid, 116).replace(/;/g, "；；");
        }
    } else {
        // 普通用户
        if (getMyVar('bf_cookie','未定义')=='未定义') {
            if (badge.match(/会员|付费/)) {
                eval(fetch('hiker://files/cache/Parse_Dn.js', {}));
                return aytmParse(input);
            } else {
                return $.require("hiker://page/api").biliLazy(aid, cid, 116).replace(/;/g, "；；");
            }  
        }else{
            // 共享会员权限
            if (badge.match(/付费/)) {
                return $(['魔断解析','已购买'], 1).select((input,aid,cid) => {
                    if (input=='魔断解析') {
                        eval(fetch('hiker://files/cache/Parse_Dn.js', {}));
                        return aytmParse(link);
                    }else{
                        return $.require("hiker://page/api").biliLazy(aid, cid, 116).replace(/;/g, "；；");
                    }
                },link,aid,cid)
            } else {
                return $.require("hiker://page/api").biliLazy(aid, cid, 116).replace(/;/g, "；；");
            }
        }
    }
});
var Color = "#19B89D";
var 每页集数 = 30;
var 搜索模式 = '分组';
var 调用名称 = '简影';
putMyVar('折叠', '关');
putMyVar('日历', '关');
putMyVar('Data', Data);
putMyVar('lazy', lazy);
// 清除变量
addListener('onClose', $.toString(() => {
    clearMyVar('分集起');
    clearMyVar('分集终');
    clearMyVar('折叠');
    clearMyVar('日历');
    clearMyVar('Data');
    clearMyVar('lazy');
    clearMyVar('剧集列表');
}))
// 设置动态最新章节
setLastChapterRule('js:' + $.toString(() => {
    eval(JSON.parse(request('hiker://page/getUpdate?rule=哔哩.cy')).rule);
}));
// 页面内容正文---
// 基本信息模块
d.push({
    title: 短讯,
    desc: 简介 == '' ? '暂无简介' : 简介.substring(0, 45) + '‘‘’’…<font color="#1C1C1C">详情</font>',
    img: 封面,
    url: $().rule((简介) => {
        res = {};
        var d = [];
        d.push({
            title: '影片简介：' + '\n\t\t\t\t\t\t\t' + 简介,
            col_type: 'long_text'
        });
        res.data = d;
        setHomeResult(res);
    }, 简介),
    col_type: 'movie_1_vertical_pic_blur',
});
// 更新日历模块
d.push({
    title: '日历',
    img: "hiker://files/rules/cy/bili_pic/日历.png",
    url: $('#noLoading#').lazyRule((追剧日历, 更新进度) => {
        if (getMyVar('日历', '关') == '开') {
            deleteItem('追剧日历');
            putMyVar('日历', '关');
        } else {
            addItemAfter('显示模式', {
                title: 追剧日历.match(/完结|期待/) ? 追剧日历 : 追剧日历 + '<br><font color="red">更新至' + (更新进度.match(/[\u4e00-\u9fa5]/) ? 更新进度 : ('第' + (更新进度 == '' ? 0 : 更新进度) + '话')) + '</font>',
                col_type: "rich_text",
                extra: {
                    id: '追剧日历'
                }
            });
            putMyVar('日历', '开');
        }
        return 'toast://已切换日历为:' + getMyVar('日历', '开')
    }, 追剧日历, 更新进度),
    col_type: 'icon_5'
});
// 订阅模块
d.push({
    title: '订阅',
    img: "hiker://files/rules/cy/bili_pic/订阅.png",
    url: $('#noLoading#').lazyRule(() => {
        selectionList = ['订阅', '取消订阅'];
        return $(selectionList, 2).select(() => {
            season_id = JSON.parse(getMyVar('Data')).season_id;
            if (input == '订阅') {
                res = $.require("hiker://page/api").subscribeFilm(season_id);
            } else {
                res = $.require("hiker://page/api").cancelSubscribeFilm(season_id);
            }
            return 'toast://' + res;
        });
    }),
    col_type: 'icon_5'
});
// 评论模块
d.push({
    title: '评论',
    img: "hiker://files/rules/cy/bili_pic/查看评论.png",
    url: $('#noLoading#').lazyRule(() => {
        return $('hiker://empty').rule(() => {
            var d = [];
            List = JSON.parse(getMyVar('Data')).episodes;
            List.forEach(item => {
                d.push({
                    title: item.share_copy + '<font color="#33cccc">	➮</font>',
                    img: item.cover + '@Referer=',
                    url: 'hiker://page/Comments?p=fypage',
                    col_type: "avatar",
                    extra: {
                        "id": item.aid,
                        "type": 1
                    }
                });
            });
            setResult(d);
        });
    }),
    col_type: 'icon_5'
});

// 精彩继续模块
d.push({
    title: '周边',
    img: "hiker://files/rules/cy/bili_pic/周边.png",
    url: $('#noLoading#').lazyRule(() => {
        Data = JSON.parse(getMyVar('Data'));
        // 有些没有直接去掉懒得处理~
        if (Data.section) {
            周边数组 = ["🏷️相关系列", "🤣PV&花絮", "🍉竖屏也精彩", "🔥精彩二创"];
        } else {
            周边数组 = ["🏷️相关系列"];
        }
        // 周边数组 = ["🏷️相关系列", "🤣PV&花絮", "竖屏也精彩", "精彩二创"];
        return $(周边数组, 1, 'ღ精彩继续').select(() => {
            Data = JSON.parse(getMyVar('Data'));
            lazy = getMyVar('lazy');
            if (input === '🏷️相关系列') {
                系列名称 = Data.season_title
                系列数据 = Data.seasons;
                return $().rule((系列数据) => {
                    d = [];
                    setPageTitle('🏷️相关系列');
                    if (系列数据 == [] || 系列数据 == '') {
                        d.push({
                            title: '‘‘’’<big><font color="red">⭐该影片没有相关系列哦~⭐</font></big>',
                            url: 'toast://都没有，，你点啥呀~',
                            desc: '敬请期待~',
                            col_type: 'text_center_1'
                        });
                    }
                    系列数据.forEach(list => {
                        d.push({
                            title: list.season_title,
                            img: (list.horizontal_cover_1610 == '' ? (list.horizontal_cover_169 == '' ? list.cover : list.horizontal_cover_169) : list.horizontal_cover_1610) + "@Referer=",
                            url: 'hiker://page/filmerji#immersiveTheme#',
                            desc: list.new_ep.index_show,
                            col_type: 'movie_1_left_pic',
                            extra: {
                                "ep_id": list.new_ep.id
                            }
                        });
                    });
                    setHomeResult(d);
                }, 系列数据)
            } else if (input === '🤣PV&花絮') {
                try {
                    PV和花絮数据 = Data.section[0].episodes;
                    return $().rule((PV和花絮数据, lazy) => {
                        d = [];
                        setPageTitle('🤣PV&花絮');
                        if (PV和花絮数据 == [] || PV和花絮数据 == '') {
                            d.push({
                                title: '‘‘’’<big><font color="red">⭐该影片/视频没有相关PV和花絮哦~⭐</font></big>',
                                url: 'toast://都没有，，你点啥呀~',
                                desc: '敬请期待~',
                                col_type: 'text_center_1'
                            });
                        }
                        PV和花絮数据.forEach(list => {
                            d.push({
                                title: list.title,
                                img: list.cover + "@Referer=",
                                url: list.link + lazy,
                                desc: list.subtitle + '\n' + list.long_title,
                                col_type: 'movie_1_left_pic',
                                extra: {
                                    id: list.link,
                                    aid: list.aid,
                                    bvid: list.bvid,
                                    cid: list.cid,
                                    badge: list.badge
                                }
                            });
                        });
                        setHomeResult(d);
                    }, PV和花絮数据, lazy)
                } catch (e) {
                    return 'toast://没有数据哦~'
                }
            } else if (input === '🍉竖屏也精彩') {
                try {
                    竖屏也精彩数据 = Data.section[1].episodes;
                    return $().rule((竖屏也精彩数据, lazy) => {
                        d = [];
                        setPageTitle('🍉竖屏也精彩');
                        if (竖屏也精彩数据 == [] || 竖屏也精彩数据 == '') {
                            d.push({
                                title: '‘‘’’<big><font color="red">⭐该影片/视频没有相关竖屏短视频哦~⭐</font></big>',
                                url: 'toast://都没有，，你点啥呀~',
                                desc: '敬请期待~',
                                col_type: 'text_center_1'
                            });
                        }
                        竖屏也精彩数据.forEach(list => {
                            d.push({
                                title: list.title,
                                img: list.cover + "@Referer=",
                                url: list.link + lazy,
                                desc: list.subtitle + '\n' + list.long_title,
                                col_type: 'movie_1_left_pic',
                                extra: {
                                    id: list.link,
                                    aid: list.aid,
                                    bvid: list.bvid,
                                    cid: list.cid,
                                    badge: list.badge
                                }
                            });
                        });
                        setHomeResult(d);
                    }, 竖屏也精彩数据, lazy)
                } catch (e) {
                    return 'toast://没有数据哦~'
                }
            } else if (input === '🔥精彩二创') {
                try {
                    精彩二创数据 = Data.section[2].episodes;
                    return $().rule((精彩二创数据, lazy) => {
                        d = [];
                        setPageTitle('🔥精彩二创');
                        if (精彩二创数据 == [] || 精彩二创数据 == '') {
                            d.push({
                                title: '‘‘’’<big><font color="red">⭐该影片/视频没有二创作品哦~⭐</font></big>',
                                url: 'toast://都没有，，你点啥呀~',
                                desc: '敬请期待~',
                                col_type: 'text_center_1'
                            });
                        }
                        精彩二创数据.forEach(list => {
                            // 针对部分参数缺失的情况如凡人修仙传写的，，
                            if (list.aid == 0 || list.cid == 0) {
                                viedeoData = JSON.parse(fetch("https://api.bilibili.com/x/web-interface/view?aid=" + list.link.split('av')[1], {
                                    headers: {
                                        "User-Agent": PC_UA,
                                        "Referer": 'https://www.bilibili.com/'
                                    },
                                    method: "GET"
                                })).data;
                                aid = viedeoData.aid;
                                bvid = viedeoData.bvid;
                                cid = viedeoData.cid;
                            } else {
                                aid = list.aid;
                                bvid = list.bvid;
                                cid = list.cid;
                            }
                            d.push({
                                title: list.title,
                                img: list.cover + "@Referer=",
                                url: list.link + lazy,
                                desc: list.subtitle + '\n' + list.long_title,
                                col_type: 'movie_1_left_pic',
                                extra: {
                                    id: list.link,
                                    aid: aid,
                                    bvid: bvid,
                                    cid: cid,
                                    badge: list.badge
                                }
                            });
                        });
                        setHomeResult(d);
                    }, 精彩二创数据, lazy)
                } catch (e) {
                    return 'toast://没有数据哦~'
                }
            }
            return "toast://正在进入" + input + "界面...";
        })
    }),
    col_type: "icon_5",
    extra: {
        id: '精彩继续'
    }
});
// 显示模式更改模块
d.push({
    title: getItem('模式', '双边'),
    img: getItem('模式', '双边') == '列表' ? "hiker://files/rules/cy/bili_pic/列表.png" : 'hiker://files/rules/cy/bili_pic/双边.png',
    url: $('#noLoading#').lazyRule(() => {
        显示的剧集列表 = JSON.parse(getMyVar('显示的剧集列表'));
        if (getItem('模式', '双边') == '列表') {
            显示的剧集列表.forEach(el => {
                updateItem(el.link, {
                    col_type: 'movie_2',
