        url = unescape(base64Decode(url));
    }
    if (/m3u8|mp4|1anime/.test(url)) {
        return url.indexOf("1anime") != -1 ? url+ ";{Referer@"+input+"}" : url
    } else if (/qq.com|youku|iqiyi|mgtv|bilibili/.test(url)) {
        eval("var config=" + fetch("hiker://files/cache/MyParseSet.json"));
        eval(fetch(config.cj));
        return aytmParse(url)
    } else if (input.indexOf("ysjdm") != -1 && from == "dplayer") {
        try {
            var code = request("https://bf.sbdm.cc/m3u8.php?url=" + url, {
                headers: {
                    "Referer": "http://ysjdm.net/"
                }
            });

            function v_decrypt(code) {
                var text = code.match(/getVideoInfo\("(.*?)"/)[1];
                var le_token = code.split("_token = \"")[1].split("\"")[0];
                eval(getCryptoJS());
                eval(fetch("https://bf.sbdm.cc/js/play.js", {
                    headers: {
                        "Referer": "https://bf.sbdm.cc/m3u8.php?url=" + url
                    }
                }));
                /*
                var _token_key = CryptoJS.enc.Utf8.parse("A42EAC0C2B408472")
                var _token_iv = CryptoJS.enc.Utf8.parse(le_token);*/
                return CryptoJS.AES.decrypt(text, _token_key, {
                    iv: _token_iv,
                    padding: CryptoJS.pad.Pkcs7
                }).toString(CryptoJS.enc.Utf8);
            }
            var video = v_decrypt(code);
            return JSON.stringify({
                urls: [video],
                headers: [{
                    'Referer': 'https://bf.sbdm.cc'
                }]
            })
        } catch (e) {
            return "video://" + input
        }
    } else {
        return "video://" + input
    }
}

