function cutstr(str, len) {
    var str_length = 0;
    var str_len = 0;
    str_cut = new String();
    str_len = str.length;
    for (var i = 0; i < str_len; i++) {
        a = str.charAt(i);
        str_length++;
        if (escape(a).length > 4) {
            //中文字符的长度经编码之后大于4
            str_length++;
        }
        str_cut = str_cut.concat(a);
        if (str_length >= len) {
            //达到目标长度，即为字符串加上省略号并返回
            str_cut = str_cut.concat("...");
            return str_cut;
        }
    }
    //如果字符串长度小于需要裁切的长度，直接返回
    if (str_length < len) {
        return str;
    }
}

