function reTag(text, i, o) {
    var r = new RegExp();
    i.forEach((x, i) => {
        r = new RegExp("(<\/?)" + x + "(.*?>)", "g");
        text = text.replace(r, '$1' + o[i] + '$2');
    })
    return text;
}

