var lazy = $('#noPre#').lazyRule(() => {
    let tag = input.split("#")[1];
    //log(tag)
    let muid = input.match(/sound\/(\d+)/)[1];
    //log(muid)
    let timestamp = new Date().getTime();

    let furl = "https://mobile.ximalaya.com/mobile-playpage/track/v3/baseInfo/" + timestamp + "?device=web&trackId=" + muid;
    let headers = {
        'User-Agent': PC_UA,
        'Cookie': "", //有会员的想自用的可以自己换此处cookie
        'Content-Type': 'application/x-www-form-urlencoded'
    };
    if (tag == 0 || tag == 1) {

        try {
            var jhtml = fetch(furl, {
                headers: headers,
                method: 'get'
            });
            var playlist = JSON.parse(jhtml);
        } catch (e) {
            var playlist = JSON.parse(fetch('http://jx.nokia.press/api6/xmly?' + 'ts=' + timestamp + '&muid=' + muid));
        }
    } else {
        var playlist = JSON.parse(fetch('http://jx.nokia.press/api6/xmly?' + 'ts=' + timestamp + '&muid=' + muid));
    }

    try {
        function base64UrlDecode(s) {
            s = s.replace(/-/g, '+').replace(/_/g, '/');
            switch (s.length % 4) {
                case 0:
                    break; // No pad chars in this case 
                case 2:
                    s += '==';
                    break; // Two pad chars 
                case 3:
                    s += '=';
                    break; // One pad char 
                default:
                    throw new Error("Illegal base64url String!");
            }
            const javaString = java.lang.String;

            let bytes = _base64.decode(s, _base64.NO_WRAP); // Standard base64 decoder
            let javaImport = new JavaImporter();
            javaImport.importPackage(
                Packages.com.alibaba.fastjson
            );

            return javaImport.JSON.toJSONString(bytes).replace('"', '') + "";

        }

        function De(url) {
            eval(getCryptoJS())
            var cipherText = base64UrlDecode(url);         
            //log(typeof(url))                        
            let res = CryptoJS.AES.decrypt(cipherText, CryptoJS.enc.Hex.parse("aaad3e4fd540b0f79dca95606e72bf93"), {
                mode: CryptoJS.mode.ECB,
                padding: CryptoJS.pad.Pkcs7
            })
            return res.toString(CryptoJS.enc.Utf8)
        }
        let playdata = playlist.trackInfo.playUrlList;
        //log(playdata)

        //多线路
        var types = [];
        var urls = [];
        for (let i = 0; i < playdata.length; i++) {
            types.push(playdata[i].type);
            urls.push(De(playdata[i].url));
        }
        //log(types)
        return JSON.stringify({
            urls: urls,
            names: types
        })

        //最优画质
        /*
        for (let i in playdata) {
            if (playdata[i].type == "M4A_128") {
                return De(playdata[i].url)
            }else if (playdata[i].type == "M4A_64") {
                return De(playdata[i].url)
            }
        }
        */

    } catch (e) {
        return "toast://付费内容，无法播放！"
    }

});

