var d = [];
if (fileExist("hiker://files/cache/alist_cache.json")) {
    let alist = storage0.getItem("list", [])
    var l = JSON.parse(fetch("hiker://files/cache/alist_cache.json")||[])
    var testlist = alist.map(function(item) {
        return item.url
    })
