    color: function(text, color) {
        return `‘‘’’<strong><font color="${color}">${text}</front></strong>`
    },
    getIcon: function(content) {
        if (content.is_dir) {
            img = "https://lanmeiguojiang.com/tubiao/more/274.png"
        } else {
            let fileName = content.name.toLowerCase()
            let hzm = $.require("api").getHzm(fileName)
            if (content.thumb && content.type != 0) {
                img = content.thumb
            } else if ($.movie.includes(hzm)) {
                img = "https://lanmeiguojiang.com/tubiao/more/30.png"
            } else if ($.audio.includes(hzm)) {
                img = "https://lanmeiguojiang.com/tubiao/more/40.png"
            } else if ($.text.includes(hzm)) {
                img = "https://lanmeiguojiang.com/tubiao/more/78.png"
            } else if ($.img.includes(hzm)) {
                img = "https://lanmeiguojiang.com/tubiao/more/38.png"
            } else if ($.yasuo.includes(hzm)) {
                img = "https://lanmeiguojiang.com/tubiao/more/201.png"
            } else if ($.office.includes(hzm)) {
                img = "https://lanmeiguojiang.com/tubiao/more/185.png"
            } else {
                img = "https://lanmeiguojiang.com/tubiao/more/239.png"
            }
        }
        return img
    },
    formatDate: function(time) {
        return time.split("T")[0] + " " + time.split("T")[1].split(".")[0].split("Z")[0].split("+")[0]
    },
    getFileSize: function(size) {
        if (size <= 0) {
            return "";
        }
        let unitForm = ["Byte", "KB", "MB", "GB", "TB"];
        for (let i = 0, len = unitForm.length; i < len; i++) {
            if (size > 1024) {
                size /= 1024;
                continue;
            } else {
                return size.toFixed(2).replace(/(\.00)$/, "") + unitForm[i];
            }
        }
        return "ERROR:数值过大";
    },
    getHzm: function(fileName) {
        return fileName.substr(fileName.lastIndexOf(".") + 1)
    },
    sorts: function(list, method) {
        if (method.includes("名称")) {
            list.sort(function(a, b) {
                return a.name.localeCompare(b.name)
            })
        } else if (method.includes("时间")) {
            list.sort(function(a, b) {
                return a.modified - b.modified
            })
        } else if (method.includes("大小")) {
            list.sort(function(a, b) {
                return a.size - b.size
            })
        }
        if (method.includes("反")) {
            list.reverse();
        }
        return list
    },
    getSubtitles: function(list, url, path) {
        const {
            getHzm
        } = $.require("api")
        let s = []
        list.forEach(item => {
            if ($.subtitle.includes(getHzm(item.name))) {
                s.push({
                    name: item.name,
                    url: encodeURI(url + "d" + (path == "/" ? "" : path) + "/" + item.name + "?sign=" + item.sign)
                })
            }
        })
        s.unshift({
            name: "不需要字幕"
        })
        return s
    }
}"},{"col_type":"movie_3","name":"搜索","path":"search","rule":"js:
