function doubleTreating(openMode, findAliUrl, title) {
    let aliUrl = input;
    let sharePwd;
    if (findAliUrl) {
        let result = findAliUrl(aliUrl);
        if (typeof result === "string")
            aliUrl = result;
        else
            aliUrl = result.aliUrl, sharePwd = result.sharePwd;
    }
    if (aliUrl.includes("www.aliyundrive.com/s/")) {
        if (openMode === "æœ¬åœ°è½¬ç ") {
            return 'hiker://page/aliyun?page=fypage&rule=' + title + '&realurl=' + encodeURIComponent(aliUrl) + "&sharePwd=" + (sharePwd || "");
        } else {
            shareId = aliUrl.split("/")[4];
            return 'smartdrive://share/browse?shareId=' + shareId + '&sharePwd=' + (sharePwd || "");
        }
    } else if (aliUrl.startsWith("http")) {
        return "web://" + aliUrl;
    } else {
        return aliUrl;
    }
}

function getModeUrl(aliUrl, sharePwd, openMode) {
    if (aliUrl.includes("www.aliyundrive.com/s/")) {
