$.exports.downloadDanMu = function(episodeId, name) {
    let danMuData = JSON.parse(request(buildUrl(basicUrl + "/api/v2/comment/" + episodeId, {
        withRelated: true,
        chConvert: 1
    })));
    let dMcontext = "";
    for (let comment of danMuData.comments) {
        //dMcontext += `<d p="${Math.round(timepoint)},${ct},${size},${color},0">${content}</d>\n`;
        let paramText = comment.p;
        let commentText = comment.m;
        if (paramText == null || commentText == null) continue;
        let params = paramText.split(",");
        let paramsT = [];
        for (let i in params) {
            if (i == 3) {
                //é¢œè‰²å€¼ä¸º0æ—¶ï¼Œé»˜è®¤è®¾ç½®ä¸ºç™½è‰²
                if (params[i] == "0" || params[i] == "-1") {
                    paramsT.push("16777215");
                    continue
                }

            }
            if (i == 2) {
                paramsT.push("25");
            }
            paramsT.push(params[i]);

        }
        paramsT.push("0", "0", "0");
        dMcontext += `<d p="${paramsT.join(',')}">${encodeHtml(commentText)}</d>\n`;
    }
    if (!dMcontext) return;
    dMcontext = `<?xml version="1.0" encoding="UTF-8"?><i>\n${dMcontext}</i>`;
    let path = sevePath + name.replace(/\//g,"_") + "_" + episodeId + ".xml";
    writeFile(path, dMcontext);
    return path;
}
$.exports.getLocalDanMu = function(episodeId, name) {
    let path = sevePath + name.replace(/\//g,"_") + "_" + episodeId + ".xml";
    if (fileExist(path)) {
        return path;
    }
}

