$.exports = {
    checkali(realurl) {
        try {
            if (realurl.includes("com/s/") && realurl != "") {
                let shareId = realurl.split("?")[0].split("/")[4];
                let share = fetch("https://api.aliyundrive.com/adrive/v3/share_link/get_share_by_anonymous", {
                    headers: {
                        'content-type': 'application/json',
                        "User-Agent": PC_UA,
                        "Referer": "https://www.aliyundrive.com/"
                    },
                    body: {
                        "share_id": shareId
                    },
                    method: 'POST'
                });
                if (share.includes("share_link is") || share.includes("share_link cannot")) return false;
            }
        } catch (e) {}
        return true;
    },
    bytesToSize(size) {
        if (!size) {
            return '0';
        }
        const units = ['B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
        let i = 0;
        while (size >= 1024) {
            size /= 1024;
            i++;
        }
        size = i ? Number(size.toFixed(2)) : size;
        return `${size} ${units[i]}`;
    },
    getCryptoJS() {
        if (typeof CryptoJS === "undefined") {
            eval(getCryptoJS());
            return CryptoJS;
        }
        return CryptoJS;
    },
    getNamesSort() {
        let chnNumChar = {
            é›¶: 0,
            ä¸€: 1,
            äºŒ: 2,
            ä¸‰: 3,
            å››: 4,
            äº”: 5,
            å…­: 6,
            ä¸ƒ: 7,
            å…«: 8,
            ä¹: 9
        };

        let chnNameValue = {
            å: {
                value: 10,
                secUnit: false
            },
            ç™¾: {
                value: 100,
                secUnit: false
            },
            åƒ: {
                value: 1000,
                secUnit: false
            },
            ä¸‡: {
                value: 10000,
                secUnit: true
            },
            äº¿: {
                value: 100000000,
                secUnit: true
            }
        }

        function ChineseToNumber(chnStr) {
            let rtn = 0;
            let section = 0;
            let number = 0;
            let secUnit = false;
            let str = chnStr.split('');

            for (let i = 0; i < str.length; i++) {
                let num = chnNumChar[str[i]];
                if (typeof num !== 'undefined') {
                    number = num;
                    if (i === str.length - 1) {
                        section += number;
                    }
                } else {
                    let unit = chnNameValue[str[i]].value;
                    secUnit = chnNameValue[str[i]].secUnit;
                    if (secUnit) {
                        section = (section + number) * unit;
                        rtn += section;
                        section = 0;
                    } else {
                        section += (number * unit);
                    }
                    number = 0;
                }
            }
            return rtn + section;
        }

        function nameCompare(a, b) {
            if (a == null || b == null)
                return a == null ? b == null ? 0 : -1 : 1;

            a = a.replace(/([é›¶ä¸€äºŒä¸‰å››äº”å…­ä¸ƒå…«ä¹åç™¾åƒä¸‡äº¿])/g, function(match, p1, p2, p3, offset, string) {
                // p1 is nondigits, p2 digits, and p3 non-alphanumerics
                return ChineseToNumber(p1);
            })
            b = b.replace(/([é›¶ä¸€äºŒä¸‰å››äº”å…­ä¸ƒå…«ä¹åç™¾åƒä¸‡äº¿])/g, function(match, p1, p2, p3, offset, string) {
                // p1 is nondigits, p2 digits, and p3 non-alphanumerics
                return ChineseToNumber(p1);
            })

            let NUMBERS = java.util.regex.Pattern.compile("(?<=\\D)(?=\\d)|(?<=\\d)(?=\\D)")
            let split1 = NUMBERS.split(new java.lang.String(a));
            let split2 = NUMBERS.split(new java.lang.String(b));

            for (let i = 0; i < Math.min(split1.length, split2.length); i++) {
                let c1 = split1[i].charCodeAt(0);
                let c2 = split2[i].charCodeAt(0);
                let cmp = 0;
                let zeroCharCode = '0'.charCodeAt(0);
                let nineCharCode = '9'.charCodeAt(0);

                if (c1 >= zeroCharCode && c1 <= nineCharCode && c2 >= zeroCharCode && c2 <= nineCharCode) {
                    cmp = new java.math.BigInteger(split1[i]).compareTo(new java.math.BigInteger(split2[i]));
                }

                if (cmp === 0) {
                    let regex = /[a-zA-Z0-9]/
                    let s1 = String(split1[i])
                    let s2 = String(split2[i])
                    if (regex.test(s1) || regex.test(s2)) {
                        cmp = new java.lang.String(split1[i]).compareTo(new java.lang.String(split2[i]));
                        // cmp = s1.localeCompare(s2, 'en')
                    } else {
                        cmp = s1.localeCompare(s2, 'zh')
                    }
                }

                if (cmp !== 0) {
                    return cmp;
                }
            }
            let lengthCmp = split1.length - split2.length;
            // if (lengthCmp !== 0) lengthCmp = lengthCmp > 0 ? -1 : 1;
            return lengthCmp;
        }
        return nameCompare;
    }
}

