$.exports.getFiles = function(path) {
    try {
        if (!path || path == "") {
            return false
        }
        let webdav = buildWebDav(path, getItem("user", ""), getItem("pass", ""))
        let res = webdav.list()
        res = JSON.parse(res)
        return res
    } catch (e) {
        log(e.message)
        return false
    }
}
$.exports.fetchFile = function(path) {
    let res = request(path, {
        headers: {
            "authorization": "Basic " + base64Encode(getItem("user") + ":" + getItem("pass"))
        }
    })
    return res
}
$.exports.getIcon = function(content) {
    if (content.dir) {
        img = "https://hikerfans.com/tubiao/more/274.png"
    } else {
        let fileName = content.name.toLowerCase()
        let hzm = $.require("code").getHzm(fileName)
        if (content.thumb && content.type != 0) {
            img = content.thumb
        } else if ($.movie.includes(hzm)) {
            img = "https://hikerfans.com/tubiao/more/30.png"
        } else if ($.audio.includes(hzm)) {
            img = "https://hikerfans.com/tubiao/more/40.png"
        } else if ($.text.includes(hzm)) {
            img = "https://hikerfans.com/tubiao/more/78.png"
        } else if ($.img.includes(hzm)) {
            img = content.url + "@headers=" + JSON.stringify({
                "authorization": "Basic " + base64Encode(getItem("user") + ":" + getItem("pass"))
            })
        } else if ($.yasuo.includes(hzm)) {
            img = "https://hikerfans.com/tubiao/more/201.png"
        } else if ($.office.includes(hzm)) {
            img = "https://hikerfans.com/tubiao/more/185.png"
        } else {
            img = "https://hikerfans.com/tubiao/more/239.png"
        }
    }
    return img
}
$.exports.getHzm = function(fileName) {
    return fileName.substr(fileName.lastIndexOf(".") + 1)
}
$.exports.removeExt = function(file) {
    return file.split('.').slice(0, -1).join('.')
};
$.exports.getSub = function(list) {
    const {
        getHzm
    } = $.require("hiker://page/code5")
    let s = []
    list.forEach(item => {
        if ($.subtitle.includes(getHzm(item.name))) {
            s.push({
                title: item.name,
                url: item.url
            })
        }
    })
    return s
}
$.exports.download = function(path, name) {
    let webdav = buildWebDav(path, getItem("user", ""), getItem("pass", ""))
    let p = "hiker://files/cache/_fileSelect_" + name;
    webdav.download(name, p);
    return getPath(p);
}
$.exports.deleteFile = function(path, name) {
    let webdav = buildWebDav(path, getItem("user", ""), getItem("pass", ""))
    webdav.delete(name)
}
$.exports.rename = function(path, oldname, newname) {
    toast("æš‚æœªæ”¯æŒ")
}
$.exports.sorts = function(list, method) {
    if (method.includes("åç§°")) {
        list.sort(function(a, b) {
            return a.name.localeCompare(b.name)
        })
    } else if (method.includes("æ—¶é—´")) {
        list.sort(function(a, b) {
            return a.modifyTimestamp - b.modifyTimestamp
        })
    }
    if (method.includes("å")) {
        list.reverse();
    }
    return list
}
