/**
 * ä½¿ç”¨è¯´æ˜ï¼š
 * 1.æå‰å»ºç«‹å¥½ åˆ†ç±»æ‰€å±ç±»å‹å’Œåˆ†ç±»åˆ—è¡¨ çš„å¯¹åº”å…³ç³»ï¼Œå³ get å‡½æ•°ä¸­çš„ categories
 * 2.è®¾ç½®å­åˆ†ç±»æ•°æ®çš„ keyï¼Œè°ƒç”¨ setSubCategoriesItemKey(titleKey, urlKey)
 * 3.ç„¶åè°ƒç”¨ CategoriesHeader.get(layout, categories, page)
 *
 * æ”¯æŒé“¾å¼è°ƒç”¨ï¼Œä¸€å¥è¯å³å¯è°ƒç”¨ç”Ÿæˆåˆ†ç±»ï¼Œå³ï¼š
 * CategoriesHeader
 *    .setSubCategoriesItemKey(titleKey, urlKey)
 *    .get(layout, categories, page)
 *
 * @type {{setSubCategoriesItemKey: CategoriesHeader.setSubCategoriesItemKey, get: CategoriesHeader.get, categoriesKey: {title: string, url: string}}}
 */
let CategoriesHeader = {
    categoriesKey: {
        sub_categories: '',
        title: '',
        url: '',
    },
    /**
     * 1.è®¾ç½®ä»åˆ†ç±»åˆ—è¡¨ä¸­æ‹¿çš„å­åˆ†ç±»çš„æ•°æ® keyï¼Œ
     *   æ ¹æ® key æ‹¿åˆ°æ•°æ®åï¼Œ
     *   ä¼šè‡ªåŠ¨èµ‹å€¼ç»™ scroll_button çš„ { title: item[titleKey], url: item[urlKey] }
     *
     * @param title_key title çš„ key
     * @param url_key url çš„ key
     */
    setSubCategoriesItemKey : (title_key, url_key) => {
        CategoriesHeader.categoriesKey.title = title_key
        CategoriesHeader.categoriesKey.url = url_key
        return CategoriesHeader;
    },
    setSubCategoriesKey: (sub_categories_key) => {
        CategoriesHeader.categoriesKey.sub_categories = sub_categories_key
        return CategoriesHeader;
    },
    /**
     * 2.è·å–åˆ†ç±»å¤´éƒ¨
     *
     * @param layout å½“å‰ç•Œé¢ï¼Œå³å¸¸ç”¨çš„ d.push çš„ d
     * @param categories åˆ†ç±»æ‰€å±ç±»å‹å’Œåˆ†ç±»åˆ—è¡¨çš„å¯¹åº”å…³ç³»è¡¨ï¼Œ
     *        å¦‚ï¼š
     *        {
     *           "å¹´ä»½": [{ "name": "2021", "value": "2021" }, { "name": "2020", "value": "2020" }...],
     *           "æ’åº": [{ "name": "çƒ­åº¦", "value": "hot" }, { "name": "æ—¶é—´", "value": "time" }...],
     *        }
     * @param page é¡µç 
     * @param urlListener é¢å¤–å¤„ç† button çš„ urlï¼Œéœ€è¦ return
     */
    get: (layout, categories, page, urlListener) => {
        // log(categories)
        // åˆ†ç±»æ‰€å±ç±»å‹çš„åˆ—è¡¨ï¼Œå¦‚ï¼š[ "å¹´ä»½", "æ’åº" ]
        let category_names = Object.keys(categories)
        let types = []
        // æ ¹æ®å¯¹åº”å…³ç³»è¡¨æ‹¿åˆ†ç±»åˆ—è¡¨
        category_names.map(category_name => {
            // è¿™é‡Œä¼šæ‹¿åˆ° å¹´ä»½ æ’åº... å…¶ä¸­ä¸€ä¸ª
            types.push(categories[category_name]);
            // è¿™é‡Œä¸‹é¢å¯¹ types çš„å€¼è¿›è¡Œå¤„ç†
        })
        categories = types;
        let init_cate = []

        for (let i = 0; i < 20; i++) {
            init_cate.push("0")
        }

        const cate_temp_json = getVar(MY_RULE.title, JSON.stringify(init_cate))
        const cate_temp = JSON.parse(cate_temp_json)

        if (parseInt(page) === 1) {
            /**
             * éå†ç¬¬ index å±‚åˆ†ç±»
             */
            categories.forEach((category, index) => {
                let sub_category_name = category_names[index]
                let sub_categories_key = CategoriesHeader.categoriesKey.sub_categories
                let sub_categories = sub_categories_key ? category[sub_categories_key] : category;
                // log(category)
                /**
                 * åœ¨ç¬¬ index å±‚åˆ†ç±»ä¸­éå†è¯¥å±‚æ‰€æœ‰å­åˆ†ç±»
                 */
                sub_categories.forEach((item, key) => {
                    let title = item[CategoriesHeader.categoriesKey.title]
                    let url = escape(item[CategoriesHeader.categoriesKey.url])
                    if (urlListener) url = urlListener.onDeal(item, sub_category_name, url)
                    layout.push({
                        title: key.toString() === cate_temp[index] ? 'â€œâ€œâ€â€<b><span style="color: #098AC1">' + title + '</span></b>' : title,
                        url: $(url).lazyRule((params) => {
                            params.cate_temp[params.index] = params.key.toString()

                            putVar(MY_RULE.title, JSON.stringify(params.cate_temp))
                            putVar(MY_RULE.url + "#" + params.sub_category_name, input)
                            refreshPage(true)
                            return "hiker://empty"
                        }, {
                            cate_temp: cate_temp,
                            index: index,
                            key: key,
                            page: page,
                            sub_category_name: sub_category_name
                        }),
                        col_type: 'scroll_button',
                    })
                })
                layout.push({
                    col_type: "blank_block"
                });
            })
        }
    }
}
