const File = java.io.File;
const {
    Files,
    Paths,
    StandardCopyOption,
    StandardOpenOption
} = java.nio.file;
const javaString = java.lang.String;
let javaScope = new JavaImporter(java.io, java.lang, java.lang.reflect, java.util.Vector);

function deleteFlies(fileName) {
    let file = new File(fileName);
    if (!file.exists()) {
        //log("åˆ é™¤æ–‡ä»¶å¤±è´¥ï¼š" + fileName + "æ–‡ä»¶ä¸å­˜åœ¨");
        return false;
    } else {
        if (file.isFile()) {
            return deleteFile(fileName);
        } else {
            return deleteDirectory(fileName);
        }

    }

}
/**
 * åˆ é™¤å•ä¸ªæ–‡ä»¶
 * 
 * @param fileName
 *            è¢«åˆ é™¤æ–‡ä»¶çš„æ–‡ä»¶å
 * @return å•ä¸ªæ–‡ä»¶åˆ é™¤æˆåŠŸè¿”å›true,å¦åˆ™è¿”å›false
 */
function deleteFile(fileName) {
    let file = new File(fileName);
    if (file.isFile() && file.exists()) {
        file.delete();
        //log("åˆ é™¤å•ä¸ªæ–‡ä»¶" + fileName + "æˆåŠŸï¼");
        return true;
    } else {
        //log("åˆ é™¤å•ä¸ªæ–‡ä»¶" + fileName + "å¤±è´¥ï¼");
        return false;
    }

}
/**
 * åˆ é™¤ç›®å½•ï¼ˆæ–‡ä»¶å¤¹ï¼‰ä»¥åŠç›®å½•ä¸‹çš„æ–‡ä»¶
 * 
 * @param dir
 *            è¢«åˆ é™¤ç›®å½•çš„æ–‡ä»¶è·¯å¾„
 * @return ç›®å½•åˆ é™¤æˆåŠŸè¿”å›true,å¦åˆ™è¿”å›false
 */
function deleteDirectory(dir) {
    // å¦‚æœdirä¸ä»¥æ–‡ä»¶åˆ†éš”ç¬¦ç»“å°¾ï¼Œè‡ªåŠ¨æ·»åŠ æ–‡ä»¶åˆ†éš”ç¬¦
    if (!dir.endsWith(File.separator)) {
        dir = dir + File.separator;
    }
    let dirFile = new File(dir);
    // å¦‚æœdirå¯¹åº”çš„æ–‡ä»¶ä¸å­˜åœ¨ï¼Œæˆ–è€…ä¸æ˜¯ä¸€ä¸ªç›®å½•ï¼Œåˆ™é€€å‡º
    if (!dirFile.exists() || !dirFile.isDirectory()) {
        //log("åˆ é™¤ç›®å½•å¤±è´¥" + dir + "ç›®å½•ä¸å­˜åœ¨ï¼");
        return false;
    }
    let flag = true;
    // åˆ é™¤æ–‡ä»¶å¤¹ä¸‹çš„æ‰€æœ‰æ–‡ä»¶(åŒ…æ‹¬å­ç›®å½•)
    let files = dirFile.listFiles();
    for (let i = 0; i < files.length; i++) {
        // åˆ é™¤å­æ–‡ä»¶
        if (files[i].isFile()) {
            flag = deleteFile(files[i].getAbsolutePath());
            if (!flag) {
                break;
            }
        } else { // åˆ é™¤å­ç›®å½•
            flag = deleteDirectory(files[i].getAbsolutePath());
            if (!flag) {
                break;
            }
        }
    }
    if (!flag) {
        //log("åˆ é™¤ç›®å½•å¤±è´¥");
        return false;
    }
    // åˆ é™¤å½“å‰ç›®å½•
    if (dirFile.delete()) {
        //log("åˆ é™¤ç›®å½•" + dir + "æˆåŠŸï¼");
        return true;
    } else {
        //log("åˆ é™¤ç›®å½•" + dir + "å¤±è´¥ï¼");
        return false;
    }
}

function getFileTime(path) {
    let file = new File(path);
    let lastModified = file.lastModified();
    let date = new Date(lastModified);
    return date.getTime();
}

function getName(path) {
    return new File(path).getName() + "";
}

function getFilePath(path, type, expand) {
    type = type || "file";
    if (!["file", "dir"].includes(type)) throw new Error("ç±»å‹é”™è¯¯");
    let fileType = type === "file" ? "isFile" : "isDirectory";
    let file = new File(path);
    let array = file.listFiles() || [];
    let pathList = [];
    for (let i = 0; i < array.length; i++) {
        if (array[i][fileType]()) {
            pathList.push({
                name: array[i].getName() + "",
                path: array[i].getPath() + ""
            });
        }
    }
    if (expand) {
        pathList = pathList.filter(it => it.name.endsWith(expand));
    }
    return pathList;
}

function renameFile(fromPath, name) {
    let fromFile = new File(fromPath);
    let toFile = new File(fromFile.getParent() + "/" + name);
    try {
        if (!fromFile.exists()) {
            return false;
        }
        if (toFile.exists()) {
            if (!deleteFlies(toPath)) {
                return false;
            }
        }
        Files.move(fromFile.toPath(), toFile.toPath(), StandardCopyOption.REPLACE_EXISTING);
        return true;
    } catch (e) {
        log(e.toString());
        return false;
    }
}

function moveFiles(fromPath, toPath) {
    let fromFile = new File(fromPath);
    let toFile = new File(toPath);
    try {
        if (!fromFile.exists()) {
            return false;
        }
        if (toFile.exists()) {
            if (!deleteFlies(toPath)) {
                return false;
            }
        }
        Files.move(fromFile.toPath(), toFile.toPath(), StandardCopyOption.REPLACE_EXISTING);
        return true;
    } catch (e) {
        log(e.toString());
        return false;
    }
}

function fileWrite(path, content) {
    writeFile("file://" + path, content)
}

function fileWriteAppend(path, content) {
    let file = new File(path);
    let paths = file.toPath();
    if (file.exists()) {
        Files.write(paths, new javaString(content).getBytes(), StandardOpenOption.APPEND);
    } else {
        writeFile("file://" + path, content);
    }
}

function getTotalSizeOfFilesInDir(file) {
    if (file.isFile()) {
        return file.length();
    }
    let children = file.listFiles();
    let total = 0;
    if (children != null) {
        for (let child of children) {
            total += getTotalSizeOfFilesInDir(child);
        }
    }
    return total;
}

function getFileSize(filePath) {
    //Byte
    let size = getTotalSizeOfFilesInDir(new File(filePath));
    if (size < 0) {
        return null;
    }
    let unitForm = ["Byte", "KB", "MB", "GB", "TB"];
    for (let i = 0, len = unitForm.length; i < len; i++) {
        if (size > 1024) {
            size /= 1024;
            continue;
        } else {
            return Math.ceil(size) + unitForm[i];
        }
    }
    return "ERROR:æ•°å€¼è¿‡å¤§";
}
