$.exports = function(pagenum, id, MY_URL) {
    let cfg = {
        ä¸€é¡µç« èŠ‚æ•°: 100,
        ç¼“å­˜ä¸ªæ•°: 15
    }

    var caches = [];

    function getCache() {
        let c = readFile("hiker://files/cache/xmly/chapter.json") || "[]";
        caches = JSON.parse(c)
        let url = MY_URL
        addListener("onRefresh", $.toString((url) => {
            let c = readFile("hiker://files/cache/xmly/chapter.json") || "[]";
            c = JSON.parse(c)
            let che = {
                url: url,
                page: 0,
                chapters: []
            };
            for (let i = 0; i < c.length; i++) {
                if (c[i].url == che.url) {
                    c[i] = che;
                    //log("clear:" + url)
                    saveFile("hiker://files/cache/xmly/chapter.json", JSON.stringify(c))
                    break
                }
            }
        }, url))
        for (let it of caches) {
            if (it.url == url) {
                return it;
            }
        }
        return {
            url: url,
            page: 0,
            chapters: []
        }
    }

    function saveCache(che) {
        //log(che.chapters.length)
        let c = caches
        if (c.length > cfg.ç¼“å­˜ä¸ªæ•°) {
            c.shift()
        }
        let exist = false
        for (let i = 0; i < c.length; i++) {
            if (c[i].url == che.url) {
                c[i] = che;
                exist = true;
                break
            }
        }
        if (!exist) {
            c.push(che)
        }
        saveFile("hiker://files/cache/xmly/chapter.json", JSON.stringify(c))
    }

    let cache = getCache();
    let page = pagenum;
    log("å®é™…é¡µæ•°ï¼š" + page)
    log("ç¼“å­˜é¡µæ•°ï¼š" + cache.page)
    if (page == cache.page) {
        //å·²å®Œç»“ï¼Œç›®å½•å…¨éƒ¨ç¼“å­˜äº†
        return cache.chapters
    }
    //è§£ææœ¬åœ°æ²¡æœ‰çš„ç¼“å­˜
    let urls = []
    let htmls = []
    //log(mp)
    for (let i = cache.page; i < page; i++) {
        let j = i + 1;
        var myurl = 'https://www.ximalaya.com/revision/album/v1/getTracksList?albumId=' + id + '&pageNum=' + j + '&sort=0&pageSize=100';
        urls.push({
            url: myurl,
            options: {
                headers: {
                    'User-Agent': PC_UA
                }
            }
        });
    }
    //log(urls)
    let d = [].concat(cache.chapters)
    htmls = htmls.concat(batchFetch(urls))
    //log(htmls)
    for (let it of htmls) {
        if (it == "") {
            break
        }
        let list = JSON.parse(it).data.tracks        
        //log(list.length)
        if (list.length == cfg.ä¸€é¡µç« èŠ‚æ•°) {
            //ç« èŠ‚é½å…¨çš„æ‰æ”¾æœ¬åœ°ç¼“å­˜
            cache.page = cache.page + 1
            cache.chapters = cache.chapters.concat(list)
        }
        d = d.concat(list)
    }
    saveCache(cache);
    return d;
}

